import{_ as o,c as t,o as i,ag as n}from"./chunks/framework.CAz5-5wf.js";const g=JSON.parse('{"title":"HelpmApp Funding Research Notes","description":"","frontmatter":{},"headers":[],"relativePath":"archive/HelpmApp Funding Research Notes.md","filePath":"archive/HelpmApp Funding Research Notes.md"}'),a={name:"archive/HelpmApp Funding Research Notes.md"};function r(s,e,l,c,p,u){return i(),t("div",null,[...e[0]||(e[0]=[n('<h1 id="helpmapp-funding-research-notes" tabindex="-1">HelpmApp Funding Research Notes <a class="header-anchor" href="#helpmapp-funding-research-notes" aria-label="Permalink to &quot;HelpmApp Funding Research Notes&quot;">​</a></h1><h2 id="_1-uk-grants-social-impact-funds" tabindex="-1">1. UK Grants &amp; Social Impact Funds <a class="header-anchor" href="#_1-uk-grants-social-impact-funds" aria-label="Permalink to &quot;1. UK Grants &amp; Social Impact Funds&quot;">​</a></h2><h3 id="dsit-digital-inclusion-innovation-fund" tabindex="-1">DSIT Digital Inclusion Innovation Fund <a class="header-anchor" href="#dsit-digital-inclusion-innovation-fund" aria-label="Permalink to &quot;DSIT Digital Inclusion Innovation Fund&quot;">​</a></h3><ul><li><strong>Status:</strong> Closed to applications as of Sep 2025, but monitoring for 2026 cycles is essential.</li><li><strong>Focus:</strong> Innovative interventions and best practice to increase digital participation.</li><li><strong>Grants:</strong> £25,000 to £500,000.</li><li><strong>Relevance:</strong> High. HelpmApp fits the &quot;innovative intervention&quot; and &quot;best practice&quot; criteria perfectly.</li></ul><h3 id="london-homelessness-foundation-lhf" tabindex="-1">London Homelessness Foundation (LHF) <a class="header-anchor" href="#london-homelessness-foundation-lhf" aria-label="Permalink to &quot;London Homelessness Foundation (LHF)&quot;">​</a></h3><ul><li><strong>Status:</strong> Active.</li><li><strong>Focus:</strong> Early-stage, higher-risk projects requiring proof of concept.</li><li><strong>Grants:</strong> Typically £20,000 - £30,000 for new projects.</li><li><strong>Relevance:</strong> Very High. HelpmApp is a London-based agency working directly with homeless people.</li></ul><h3 id="bethnal-green-ventures-bgv-tech-for-good" tabindex="-1">Bethnal Green Ventures (BGV) - Tech for Good <a class="header-anchor" href="#bethnal-green-ventures-bgv-tech-for-good" aria-label="Permalink to &quot;Bethnal Green Ventures (BGV) - Tech for Good&quot;">​</a></h3><ul><li><strong>Status:</strong> Applications open for Spring 2026 (Deadline: 4 Jan 2026).</li><li><strong>Offer:</strong> £60,000 investment for 7% equity + 6-week accelerator.</li><li><strong>Criteria:</strong> Innovative tech, scalability, intentionality, direct impact.</li><li><strong>Relevance:</strong> High for scaling, but requires &quot;Company Limited by Shares&quot; structure (may conflict with CIC if not structured as a subsidiary).</li></ul><h2 id="_2-borough-specific-local-innovation" tabindex="-1">2. Borough-Specific &amp; Local Innovation <a class="header-anchor" href="#_2-borough-specific-local-innovation" aria-label="Permalink to &quot;2. Borough-Specific &amp; Local Innovation&quot;">​</a></h2><h3 id="westminster-city-council-digitall-project" tabindex="-1">Westminster City Council - DigitALL Project <a class="header-anchor" href="#westminster-city-council-digitall-project" aria-label="Permalink to &quot;Westminster City Council - DigitALL Project&quot;">​</a></h3><ul><li><strong>Status:</strong> Active (Nov 2024).</li><li><strong>Focus:</strong> Digital inclusion sessions.</li><li><strong>Funding:</strong> £15,000 shared among partners.</li><li><strong>Relevance:</strong> Medium. Good for localized pilot funding.</li></ul><h3 id="camden-council-rough-sleeping-support" tabindex="-1">Camden Council - Rough Sleeping Support <a class="header-anchor" href="#camden-council-rough-sleeping-support" aria-label="Permalink to &quot;Camden Council - Rough Sleeping Support&quot;">​</a></h3><ul><li><strong>Status:</strong> Active (Oct 2025).</li><li><strong>Funding:</strong> £680k boost for support services.</li><li><strong>Relevance:</strong> High. HelpmApp can position itself as a digital layer for these services.</li></ul><h2 id="_3-corporate-csr-sponsorship" tabindex="-1">3. Corporate CSR &amp; Sponsorship <a class="header-anchor" href="#_3-corporate-csr-sponsorship" aria-label="Permalink to &quot;3. Corporate CSR &amp; Sponsorship&quot;">​</a></h2><h3 id="slaughter-and-may" tabindex="-1">Slaughter and May <a class="header-anchor" href="#slaughter-and-may" aria-label="Permalink to &quot;Slaughter and May&quot;">​</a></h3><ul><li><strong>Focus:</strong> Helping people experiencing homelessness in London.</li><li><strong>Partner:</strong> Single Homeless Project (SHP).</li><li><strong>Opportunity:</strong> Potential for pro-bono legal support or direct sponsorship of the rewards pool.</li></ul><h3 id="fitzrovia-partnership" tabindex="-1">Fitzrovia Partnership <a class="header-anchor" href="#fitzrovia-partnership" aria-label="Permalink to &quot;Fitzrovia Partnership&quot;">​</a></h3><ul><li><strong>Focus:</strong> Supporting homeless and vulnerable people in London (Fitzrovia).</li><li><strong>Opportunity:</strong> Localized sponsorship for rewards in the Central London area.</li></ul><h3 id="crisis-centrepoint-st-mungo-s" tabindex="-1">Crisis / Centrepoint / St Mungo&#39;s <a class="header-anchor" href="#crisis-centrepoint-st-mungo-s" aria-label="Permalink to &quot;Crisis / Centrepoint / St Mungo&#39;s&quot;">​</a></h3><ul><li><strong>Opportunity:</strong> These major charities have corporate partnership teams. HelpmApp can partner with them to provide the digital &quot;check-in&quot; and rewards layer for their existing service users.</li></ul>',20)])])}const d=o(a,[["render",r]]);export{g as __pageData,d as default};
