import{_ as e,c as r,o as a,ag as d}from"./chunks/framework._dqZt2de.js";const u=JSON.parse('{"title":"HelpmApp Financial Projections (Reset Edition)","description":"","frontmatter":{},"headers":[],"relativePath":"archive/financial_projections_draft.md","filePath":"archive/financial_projections_draft.md"}'),n={name:"archive/financial_projections_draft.md"};function i(o,t,s,l,c,p){return a(),r("div",null,[...t[0]||(t[0]=[d('<h1 id="helpmapp-financial-projections-reset-edition" tabindex="-1">HelpmApp Financial Projections (Reset Edition) <a class="header-anchor" href="#helpmapp-financial-projections-reset-edition" aria-label="Permalink to &quot;HelpmApp Financial Projections (Reset Edition)&quot;">​</a></h1><p><strong>Version:</strong> 2.0<br><strong>Last Updated:</strong> 17 Nov 2025<br><strong>Status:</strong> Core product being rebuilt; projections assume relaunch Month 3 post-reset</p><hr><h2 id="_1-modelling-approach" tabindex="-1">1. Modelling Approach <a class="header-anchor" href="#_1-modelling-approach" aria-label="Permalink to &quot;1. Modelling Approach&quot;">​</a></h2><ul><li>Focused on <strong>operational economics</strong>; engineering rebuild funded separately by founders/grants-in-kind.</li><li>36-month horizon starting Month 0 = reset kickoff.</li><li>Conservative case used for grant applications; upside scenario kept in appendix (not yet published).</li><li>Revenue recognised only after contractual sign-off; sponsorship in-kind tracked but excluded from P&amp;L except where cash equivalent offsets rewards spend.</li></ul><h2 id="_2-key-assumptions" tabindex="-1">2. Key Assumptions <a class="header-anchor" href="#_2-key-assumptions" aria-label="Permalink to &quot;2. Key Assumptions&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Area</th><th>Year 1 (Months 1-12)</th><th>Year 2 (Months 13-24)</th><th>Year 3 (Months 25-36)</th></tr></thead><tbody><tr><td>Active users (monthly avg)</td><td>250 rising to 500</td><td>1,200 rising to 2,000</td><td>3,500 rising to 5,000</td></tr><tr><td>Check-ins per user / month</td><td>4</td><td>5</td><td>6</td></tr><tr><td>Rewards redemption rate</td><td>60% of stars</td><td>63%</td><td>65%</td></tr><tr><td>Providers (premium)</td><td>50 total (10 premium @ £100/mo)</td><td>100 (30 premium @ £120/mo)</td><td>200 (75 premium @ £150/mo)</td></tr><tr><td>Borough / public contracts</td><td>Pilot MoUs (no cash)</td><td>3 boroughs (£30k ea) + GLA (£50k)</td><td>6 boroughs (£35k ea) + GLA (£60k) + 2 NHS (£25k ea)</td></tr><tr><td>Corporate sponsors (cash)</td><td>2 @ £5k</td><td>5 @ £10k</td><td>10 @ £15k</td></tr><tr><td>Corporate sponsors (in-kind)</td><td>£10k</td><td>£45k</td><td>£95k</td></tr><tr><td>Grant funding</td><td>£30k (UnLtd + Digital Inclusion Fund tranche)</td><td>£50k</td><td>£40k</td></tr></tbody></table><h2 id="_3-revenue-forecast-£" tabindex="-1">3. Revenue Forecast (£) <a class="header-anchor" href="#_3-revenue-forecast-£" aria-label="Permalink to &quot;3. Revenue Forecast (£)&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Source</th><th>Year 1</th><th>Year 2</th><th>Year 3</th></tr></thead><tbody><tr><td>Grant funding</td><td>30,000</td><td>50,000</td><td>40,000</td></tr><tr><td>Service provider subscriptions</td><td>12,000</td><td>43,200</td><td>135,000</td></tr><tr><td>Public sector contracts</td><td>0</td><td>140,000</td><td>290,000</td></tr><tr><td>Corporate cash sponsors</td><td>10,000</td><td>50,000</td><td>150,000</td></tr><tr><td>Other (events, consulting)</td><td>0</td><td>5,000</td><td>20,000</td></tr><tr><td><strong>Total Revenue</strong></td><td><strong>52,000</strong></td><td><strong>288,200</strong></td><td><strong>635,000</strong></td></tr></tbody></table><blockquote><p><em>In-kind sponsorship value (non-cash) estimated at £10k, £45k, £95k for Years 1-3 respectively; tracked separately for impact reports.</em></p></blockquote><h2 id="_4-expense-forecast-£" tabindex="-1">4. Expense Forecast (£) <a class="header-anchor" href="#_4-expense-forecast-£" aria-label="Permalink to &quot;4. Expense Forecast (£)&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Category</th><th>Year 1</th><th>Year 2</th><th>Year 3</th></tr></thead><tbody><tr><td>Marketing &amp; User Acquisition</td><td>25,000</td><td>60,000</td><td>90,000</td></tr><tr><td>Rewards Pool (cash spend)</td><td>20,000</td><td>55,000</td><td>120,000</td></tr><tr><td>Technology &amp; Operations</td><td>12,000</td><td>30,000</td><td>45,000</td></tr><tr><td>Staffing (part/full-time mix)</td><td>8,000</td><td>100,000</td><td>220,000</td></tr><tr><td>Partnerships &amp; BD</td><td>10,000</td><td>25,000</td><td>40,000</td></tr><tr><td>Data &amp; Impact Reporting</td><td>5,000</td><td>15,000</td><td>25,000</td></tr><tr><td>Admin, legal, contingency</td><td>5,000</td><td>8,000</td><td>15,000</td></tr><tr><td><strong>Total Expenses</strong></td><td><strong>85,000</strong></td><td><strong>293,000</strong></td><td><strong>555,000</strong></td></tr></tbody></table><blockquote><p><em>Year 1 staffing assumes founders + stipends/contractors; Year 2 adds Partnerships Manager + Community Lead; Year 3 adds full support + engineering roles.</em></p></blockquote><h2 id="_5-cash-position-funding-gap-£" tabindex="-1">5. Cash Position &amp; Funding Gap (£) <a class="header-anchor" href="#_5-cash-position-funding-gap-£" aria-label="Permalink to &quot;5. Cash Position &amp; Funding Gap (£)&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Year</th><th>Revenue</th><th>Expenses</th><th>Surplus / (Deficit)</th><th>Cumulative Cash (before new financing)</th></tr></thead><tbody><tr><td>Year 1</td><td>52,000</td><td>85,000</td><td><strong>-33,000</strong></td><td>-33,000</td></tr><tr><td>Year 2</td><td>288,200</td><td>293,000</td><td><strong>-4,800</strong></td><td>-37,800</td></tr><tr><td>Year 3</td><td>635,000</td><td>555,000</td><td><strong>+80,000</strong></td><td>+42,200</td></tr></tbody></table><p><strong>Funding Need:</strong> £70,000 working capital facility/grants to cover cumulative deficit through Year 2; repaid via Year 3 surplus.</p><h2 id="_6-unit-economics-year-2-steady-state" tabindex="-1">6. Unit Economics (Year 2 steady state) <a class="header-anchor" href="#_6-unit-economics-year-2-steady-state" aria-label="Permalink to &quot;6. Unit Economics (Year 2 steady state)&quot;">​</a></h2><ul><li><strong>CAC (blended):</strong> £45 per active user (marketing + outreach / new users per year).</li><li><strong>Rewards cost per active user:</strong> ~£27/year (based on redemption + sponsor mix).</li><li><strong>LTV (Year 2+):</strong> £180/user (assuming 24-month relationship, mix of sponsorship + LA revenue share attributable per user).</li><li><strong>Provider ARPU:</strong> £1,440/year (premium) vs £0 (basic). Target premium penetration 30-40% by Year 3.</li></ul><h2 id="_7-sensitivities" tabindex="-1">7. Sensitivities <a class="header-anchor" href="#_7-sensitivities" aria-label="Permalink to &quot;7. Sensitivities&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Variable</th><th>-10% Scenario</th><th>+10% Scenario</th><th>Notes</th></tr></thead><tbody><tr><td>Active users</td><td>Revenue -6% (due to contract weighting)</td><td>Revenue +6%</td><td>Contracts drive majority of income from Year 2</td></tr><tr><td>Rewards redemption</td><td>Expense -4k / +4k</td><td>Expense +4k</td><td>Manage via dynamic star pricing</td></tr><tr><td>Borough contracts</td><td>Revenue -140k if delayed by 1 year</td><td>+70k if GLA ups scope</td><td>Hedge with corporate bundles</td></tr><tr><td>Sponsor cash</td><td>-10k impact / +10k impact</td><td></td><td>Maintain diversified pipeline</td></tr></tbody></table><h2 id="_8-capital-plan-timeline" tabindex="-1">8. Capital Plan &amp; Timeline <a class="header-anchor" href="#_8-capital-plan-timeline" aria-label="Permalink to &quot;8. Capital Plan &amp; Timeline&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Month</th><th>Milestone</th><th>Funding Source</th></tr></thead><tbody><tr><td>0-2</td><td>Submit UnLtd &amp; Digital Inclusion Fund</td><td>Grants £35k</td></tr><tr><td>3</td><td>Secure bridge (founder loan/angel) if grant timing slips</td><td>£10-15k</td></tr><tr><td>4-6</td><td>National Lottery Partnerships application</td><td>£25-40k multi-year</td></tr><tr><td>9</td><td>Close first corporate sponsorship bundle</td><td>£10k</td></tr><tr><td>12</td><td>Negotiate borough pilot contracts for Year 2 revenue</td><td>£90k total</td></tr></tbody></table><h2 id="_9-reporting-compliance" tabindex="-1">9. Reporting &amp; Compliance <a class="header-anchor" href="#_9-reporting-compliance" aria-label="Permalink to &quot;9. Reporting &amp; Compliance&quot;">​</a></h2><ul><li>Maintain grant-specific budgets + evidence folders (receipts, impact metrics).</li><li>Quarterly management accounts shared with advisory board.</li><li>Annual independent review once revenue exceeds £250k.</li></ul><h2 id="_10-next-finance-actions" tabindex="-1">10. Next Finance Actions <a class="header-anchor" href="#_10-next-finance-actions" aria-label="Permalink to &quot;10. Next Finance Actions&quot;">​</a></h2><ol><li>Finalise cashflow tracker aligning rebuild sprints to expense forecast.</li><li>Prepare grant budget templates referencing this model.</li><li>Draft pricing sheet for provider subscriptions + dashboards.</li><li>Build sponsor pipeline CRM (tiered offers, deadlines, reporting needs).</li></ol><hr><p><em>Questions → <a href="mailto:finance@helpmapp.org" target="_blank" rel="noreferrer">finance@helpmapp.org</a></em></p>',28)])])}const g=e(n,[["render",i]]);export{u as __pageData,g as default};
