import{_ as e,c as r,o as a,ag as d}from"./chunks/framework._dqZt2de.js";const u=JSON.parse('{"title":"HelpmApp Funding Strategy (Reset Edition)","description":"","frontmatter":{},"headers":[],"relativePath":"archive/funding_strategy_v1.md","filePath":"archive/funding_strategy_v1.md"}'),i={name:"archive/funding_strategy_v1.md"};function n(o,t,s,l,h,c){return a(),r("div",null,[...t[0]||(t[0]=[d('<h1 id="helpmapp-funding-strategy-reset-edition" tabindex="-1">HelpmApp Funding Strategy (Reset Edition) <a class="header-anchor" href="#helpmapp-funding-strategy-reset-edition" aria-label="Permalink to &quot;HelpmApp Funding Strategy (Reset Edition)&quot;">​</a></h1><p><strong>Version:</strong> 2.0<br><strong>Last Updated:</strong> 17 Nov 2025<br><strong>Goal:</strong> Secure £70,000 Year-1 working capital + line of sight to £200k total over 3 years to fuel the rebuild, relaunch, and scale plan.</p><hr><h2 id="_1-funding-objectives-by-phase" tabindex="-1">1. Funding Objectives by Phase <a class="header-anchor" href="#_1-funding-objectives-by-phase" aria-label="Permalink to &quot;1. Funding Objectives by Phase&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Phase</th><th>Months</th><th>Objective</th><th>Amount</th><th>Instruments</th></tr></thead><tbody><tr><td>Reset &amp; Rebuild</td><td>0-3</td><td>Cover operational burn while product is rebuilt</td><td>£40k</td><td>Grants (UnLtd, Digital Inclusion), founder bridge</td></tr><tr><td>Pilot Launch</td><td>4-9</td><td>Fund user/provider acquisition, rewards pool, partnerships</td><td>£30k</td><td>Grants (Lottery), CSR sponsors, impact angels</td></tr><tr><td>Scale Prep</td><td>10-18</td><td>Lock multi-year contracts + larger grants for expansion</td><td>£100k+</td><td>Borough contracts, GLA/NHS deals, corporate bundles</td></tr></tbody></table><h2 id="_2-priority-funding-sources-next-90-days" tabindex="-1">2. Priority Funding Sources (Next 90 Days) <a class="header-anchor" href="#_2-priority-funding-sources-next-90-days" aria-label="Permalink to &quot;2. Priority Funding Sources (Next 90 Days)&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Source</th><th>Amount</th><th>Status</th><th>Actions</th></tr></thead><tbody><tr><td><strong>UnLtd Scaling Up Award</strong></td><td>£10-15k grant + support</td><td>High fit (application drafting)</td><td>Attend info session, book grant manager call, submit by Dec 2025</td></tr><tr><td><strong>Digital Inclusion Innovation Fund (DSIT)</strong></td><td>£25-40k grant</td><td>Medium fit (awaiting open call)</td><td>Prepare evidence pack (digital exclusion stats, rebuild plan), monitor announcement, pre-write narrative</td></tr><tr><td><strong>National Lottery Community Fund – Partnerships</strong></td><td>£20-30k (multi-year)</td><td>Medium fit (prep stage)</td><td>Draft concept note emphasising borough collaboration + data insights; seek partner letters</td></tr><tr><td><strong>Corporate CSR Bundles</strong></td><td>£10k cash + £10k in-kind target</td><td>Pipeline building</td><td>Craft sponsorship tiers (Bronze/Silver/Gold), reach out to existing contacts (cafés, supermarkets, telcos) for LOIs</td></tr><tr><td><strong>Founder/Angel Bridge</strong></td><td>£5-10k (if grant timing slips)</td><td>Contingency</td><td>Prepare note convertible outline, identify 1-2 mission-aligned angels</td></tr></tbody></table><h2 id="_3-secondary-sources-6-12-months" tabindex="-1">3. Secondary Sources (6-12 Months) <a class="header-anchor" href="#_3-secondary-sources-6-12-months" aria-label="Permalink to &quot;3. Secondary Sources (6-12 Months)&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Source</th><th>Rationale</th><th>Trigger</th></tr></thead><tbody><tr><td><strong>Homeless Link Grants</strong></td><td>Sector credibility + £10-20k over 2 years</td><td>Apply alongside member org once MVP live</td></tr><tr><td><strong>Comic Relief / Paul Hamlyn “Tech for Good”</strong></td><td>Strong fit for rebuild/scale once user data available</td><td>Revisit when pilot impact data ready</td></tr><tr><td><strong>Nesta Innovation</strong></td><td>Supports scaling evidence-based social tech</td><td>Target Year 2 after borough pilot data</td></tr><tr><td><strong>Big Issue Invest / Lloyds Bank Foundation</strong></td><td>Larger repayable/unrestricted funds</td><td>Pursue post-£100k revenue milestone</td></tr></tbody></table><h2 id="_4-sponsorship-rewards-capital-plan" tabindex="-1">4. Sponsorship &amp; Rewards Capital Plan <a class="header-anchor" href="#_4-sponsorship-rewards-capital-plan" aria-label="Permalink to &quot;4. Sponsorship &amp; Rewards Capital Plan&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Tier</th><th>Contribution</th><th>Benefits</th></tr></thead><tbody><tr><td>Bronze</td><td>£2k cash or equivalent</td><td>Logo in app, quarterly impact report</td></tr><tr><td>Silver</td><td>£5k cash/in-kind</td><td>Bronze + feature story + staff volunteering day</td></tr><tr><td>Gold</td><td>£10k+ cash/in-kind</td><td>Silver + co-branded rewards, custom dashboard</td></tr></tbody></table><p><strong>Pipeline Targets:</strong></p><ul><li>Local cafés/restaurants (meal vouchers)</li><li>Retail/pharmacy chains (hygiene kits)</li><li>Transport providers (travel passes)</li><li>Mobile network operators (top-up credit)</li><li>Corporate CSR teams seeking measurable impact</li></ul><h2 id="_5-proposal-grant-packet-checklist" tabindex="-1">5. Proposal/Grant Packet Checklist <a class="header-anchor" href="#_5-proposal-grant-packet-checklist" aria-label="Permalink to &quot;5. Proposal/Grant Packet Checklist&quot;">​</a></h2><ol><li>Updated Executive Summary + Quick Reference (Version 2.0)</li><li>Market Analysis excerpts (problem/need, user segments)</li><li>Operational Plan milestones + KPI tables</li><li>Financial Projections (3-year) with funding ask usage table</li><li>Impact Measurement Framework summary</li><li>Letters of support (service providers, borough contacts, lived-experience advisors)</li><li>Evidence of previous deployment + testimonials (even if legacy)</li></ol><h2 id="_6-timeline-owners" tabindex="-1">6. Timeline &amp; Owners <a class="header-anchor" href="#_6-timeline-owners" aria-label="Permalink to &quot;6. Timeline &amp; Owners&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Week</th><th>Action</th><th>Owner</th></tr></thead><tbody><tr><td>Wk 1</td><td>Finalise UnLtd application draft, gather attachments</td><td>Founder + Partnerships</td></tr><tr><td>Wk 2</td><td>Submit UnLtd; schedule Digital Inclusion Fund consultation call</td><td>Founder</td></tr><tr><td>Wk 3</td><td>Draft National Lottery concept note, request letters</td><td>Partnerships</td></tr><tr><td>Wk 4</td><td>Publish sponsorship prospectus, start outreach</td><td>Partnerships + Comms</td></tr><tr><td>Wk 5-6</td><td>Prepare pitch materials for angels (if needed)</td><td>Founder + Finance</td></tr><tr><td>Monthly</td><td>Track pipeline status, update CRM, report to advisory board</td><td>Partnerships</td></tr></tbody></table><h2 id="_7-messaging-pillars-for-funders" tabindex="-1">7. Messaging Pillars for Funders <a class="header-anchor" href="#_7-messaging-pillars-for-funders" aria-label="Permalink to &quot;7. Messaging Pillars for Funders&quot;">​</a></h2><ol><li><strong>De-risked build:</strong> Product already proven; reset ensures stronger architecture and impact measurement.</li><li><strong>Immediate social return:</strong> £70k funds direct user acquisition, rewards, and partnerships, not speculative dev work.</li><li><strong>Evidence-ready:</strong> Clear KPIs, impact framework, and dashboards allow funders to see results quickly.</li><li><strong>Scalable model:</strong> Diversified revenue streams (subscriptions, contracts, sponsorship) lead to sustainability by Year 3.</li></ol><h2 id="_8-risk-mitigation" tabindex="-1">8. Risk Mitigation <a class="header-anchor" href="#_8-risk-mitigation" aria-label="Permalink to &quot;8. Risk Mitigation&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Risk</th><th>Mitigation</th></tr></thead><tbody><tr><td>Grant timing delays</td><td>Maintain bridge options (founder loan/angel), stagger apps</td></tr><tr><td>Sponsor attrition</td><td>Build multi-sector pipeline, offer flexible packages</td></tr><tr><td>Matching requirements</td><td>Prioritise grants without match; if required, use in-kind contributions</td></tr><tr><td>Reporting load</td><td>Centralise data collection, assign dedicated owner</td></tr></tbody></table><h2 id="_9-tracking-template-summary" tabindex="-1">9. Tracking Template (Summary) <a class="header-anchor" href="#_9-tracking-template-summary" aria-label="Permalink to &quot;9. Tracking Template (Summary)&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Lead</th><th>Type</th><th>Amount</th><th>Stage</th><th>Next Action</th></tr></thead><tbody><tr><td>UnLtd</td><td>Grant</td><td>£12k</td><td>Draft</td><td>Submit by Dec 15</td></tr><tr><td>DSIT Digital Inclusion</td><td>Grant</td><td>£30k</td><td>Prep</td><td>Await call, pre-fill form</td></tr><tr><td>Lottery Partnerships</td><td>Grant</td><td>£25k</td><td>Concept</td><td>Gather letters</td></tr><tr><td>Sponsor A (Cafe chain)</td><td>CSR cash</td><td>£5k</td><td>Intro</td><td>Send deck, schedule meeting</td></tr><tr><td>Sponsor B (Telco)</td><td>In-kind</td><td>£3k</td><td>Warm</td><td>Share reward catalogue</td></tr><tr><td>Angel 1</td><td>Convertible</td><td>£10k</td><td>Prospect</td><td>Prepare note terms</td></tr></tbody></table><hr><p><em>Questions → <a href="mailto:funding@helpmapp.org" target="_blank" rel="noreferrer">funding@helpmapp.org</a></em></p>',25)])])}const g=e(i,[["render",n]]);export{u as __pageData,g as default};
