import{_ as e,c as r,o as a,ag as d}from"./chunks/framework._dqZt2de.js";const u=JSON.parse('{"title":"HelpmApp Operational Plan (Reset Edition)","description":"","frontmatter":{},"headers":[],"relativePath":"archive/operational_plan_draft.md","filePath":"archive/operational_plan_draft.md"}'),n={name:"archive/operational_plan_draft.md"};function i(o,t,s,l,h,p){return a(),r("div",null,[...t[0]||(t[0]=[d('<h1 id="helpmapp-operational-plan-reset-edition" tabindex="-1">HelpmApp Operational Plan (Reset Edition) <a class="header-anchor" href="#helpmapp-operational-plan-reset-edition" aria-label="Permalink to &quot;HelpmApp Operational Plan (Reset Edition)&quot;">​</a></h1><p><strong>Version:</strong> 2.0<br><strong>Last Updated:</strong> 17 Nov 2025<br><strong>Scope:</strong> 18-month roadmap aligned with clean-slate rebuild</p><hr><h2 id="_1-guiding-principles" tabindex="-1">1. Guiding Principles <a class="header-anchor" href="#_1-guiding-principles" aria-label="Permalink to &quot;1. Guiding Principles&quot;">​</a></h2><ol><li><strong>Build-small, prove-fast:</strong> Ship a lean MVP with automated data ingest and analytics hooks before adding advanced features.</li><li><strong>Lived-experience co-design:</strong> Embed peer advocates and service partners into each phase for continuous validation.</li><li><strong>Data &amp; rewards first:</strong> Engagement incentives and trustworthy service data are non-negotiable launch requirements.</li><li><strong>Transparent reset narrative:</strong> Keep partners and funders updated monthly on rebuild progress, blockers, and learnings.</li></ol><h2 id="_2-phased-roadmap-18-months" tabindex="-1">2. Phased Roadmap (18 Months) <a class="header-anchor" href="#_2-phased-roadmap-18-months" aria-label="Permalink to &quot;2. Phased Roadmap (18 Months)&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Phase</th><th>Months</th><th>Primary Goals</th><th>Success Metrics</th></tr></thead><tbody><tr><td><strong>Foundation</strong></td><td>1-6</td><td>Rebuild MVP stack, restart user/provider acquisition in 3 pilot boroughs, launch rewards v1</td><td>500 active users, 50 providers (10 premium), £10k rewards value</td></tr><tr><td><strong>Growth</strong></td><td>7-12</td><td>Expand across London, ship dashboards v1, secure borough pilots &amp; sponsor base</td><td>2,000 users, 100 providers, 3 borough pilots, 7 sponsors</td></tr><tr><td><strong>Scale</strong></td><td>13-18</td><td>Enter Manchester/Birmingham, launch native apps &amp; API, mature analytics</td><td>5,000 users, 200 providers, 360k check-ins, £200k public-sector pipeline</td></tr></tbody></table><h2 id="_3-workstreams-milestones" tabindex="-1">3. Workstreams &amp; Milestones <a class="header-anchor" href="#_3-workstreams-milestones" aria-label="Permalink to &quot;3. Workstreams &amp; Milestones&quot;">​</a></h2><h3 id="_3-1-product-engineering" tabindex="-1">3.1 Product &amp; Engineering <a class="header-anchor" href="#_3-1-product-engineering" aria-label="Permalink to &quot;3.1 Product &amp; Engineering&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Month</th><th>Deliverable</th><th>Notes</th></tr></thead><tbody><tr><td>1</td><td>Architecture RFC covering Next.js/Expo client, API layer, database choice (PlanetScale or Supabase)</td><td>Include security, data privacy, and offline strategy</td></tr><tr><td>2</td><td>Automated service ingest + admin tools</td><td>Pull from London Atlas/CSV + manual overrides</td></tr><tr><td>3</td><td>MVP release (map/list, check-ins, rewards ledger, offline cache)</td><td>Internal alpha + partner demos</td></tr><tr><td>4</td><td>Check-in verification (geo + timestamps), analytics events schema</td><td>Foundation for dashboards</td></tr><tr><td>5</td><td>Service provider portal (profile edits, update SLA reminders)</td><td>Begin premium upsell</td></tr><tr><td>6</td><td>Rewards management backend + fraud monitoring</td><td>Support sponsor SLAs</td></tr><tr><td>9</td><td>Provider dashboards v1 + borough reporting templates</td><td>Data export &amp; automated emails</td></tr><tr><td>12</td><td>Push notifications, waitlist/booking pilots</td><td>Based on partner needs</td></tr><tr><td>15</td><td>Native apps + AI recommendation beta + open API</td><td>Powered by usage data</td></tr></tbody></table><h3 id="_3-2-partnerships-community" tabindex="-1">3.2 Partnerships &amp; Community <a class="header-anchor" href="#_3-2-partnerships-community" aria-label="Permalink to &quot;3.2 Partnerships &amp; Community&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Phase</th><th>Key Actions</th></tr></thead><tbody><tr><td>Foundation</td><td>Reconfirm pilot commitments with St Mungo&#39;s, The Passage, Westminster, Camden, Lambeth; recruit 5 peer advocates (stipend + training); secure 3 sponsor LOIs</td></tr><tr><td>Growth</td><td>Host quarterly provider forums, formalise borough data-sharing agreements, expand advocates to 10, launch referral bonus campaigns</td></tr><tr><td>Scale</td><td>Enter Manchester/Birmingham networks (Homeless Link, GMCA), negotiate multi-borough contracts, spin up local sponsor pods</td></tr></tbody></table><h3 id="_3-3-marketing-communications" tabindex="-1">3.3 Marketing &amp; Communications <a class="header-anchor" href="#_3-3-marketing-communications" aria-label="Permalink to &quot;3.3 Marketing &amp; Communications&quot;">​</a></h3><ul><li><strong>Month 1:</strong> Publish reset announcement + roadmap on site/Telegram.</li><li><strong>Month 2-3:</strong> Launch targeted outreach kit (posters, QR codes, scripts) for service partners.</li><li><strong>Month 4:</strong> Release impact teaser report using legacy data + rebuild metrics to support fundraising.</li><li><strong>Month 6:</strong> Publish “First 500 Users” milestone story + case studies.</li><li><strong>Ongoing:</strong> Monthly partner bulletin, social proof series, media pitches (Inside Housing, BBC London) timed with releases.</li></ul><h3 id="_3-4-funding-governance" tabindex="-1">3.4 Funding &amp; Governance <a class="header-anchor" href="#_3-4-funding-governance" aria-label="Permalink to &quot;3.4 Funding &amp; Governance&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Activity</th><th>Timing</th></tr></thead><tbody><tr><td>Submit UnLtd Scaling Up + Digital Inclusion Fund applications</td><td>Month 1-2</td></tr><tr><td>Prepare National Lottery Partnerships proposal (multi-year)</td><td>Month 3</td></tr><tr><td>Formalise advisory board charter + quarterly meeting cadence</td><td>Month 2</td></tr><tr><td>Implement privacy/data ethics review with lived-experience reps</td><td>Month 4</td></tr><tr><td>Publish annual impact + governance report</td><td>Month 12</td></tr></tbody></table><h2 id="_4-resourcing-plan" tabindex="-1">4. Resourcing Plan <a class="header-anchor" href="#_4-resourcing-plan" aria-label="Permalink to &quot;4. Resourcing Plan&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Role</th><th>Status</th><th>Notes</th></tr></thead><tbody><tr><td>Product Lead / CTO</td><td>Founding team</td><td>Owns rebuild &amp; architecture</td></tr><tr><td>Full-stack Engineer (contract)</td><td>Needed Months 1-6</td><td>Focus on service ingest + dashboards</td></tr><tr><td>UX/Research Lead</td><td>Fractional</td><td>Conducts user testing with advocates</td></tr><tr><td>Partnerships Manager</td><td>Hire Month 4</td><td>Manages providers, borough pilots</td></tr><tr><td>Community &amp; Support Coordinator</td><td>Hire Month 5</td><td>Runs Telegram, support, advocates</td></tr><tr><td>Data Analyst (contract)</td><td>Months 8-12</td><td>Dashboards + procurement deliverables</td></tr></tbody></table><h2 id="_5-kpis-monitoring" tabindex="-1">5. KPIs &amp; Monitoring <a class="header-anchor" href="#_5-kpis-monitoring" aria-label="Permalink to &quot;5. KPIs &amp; Monitoring&quot;">​</a></h2><table tabindex="0"><thead><tr><th>KPI</th><th>Phase 1 Target</th><th>Phase 2 Target</th><th>Phase 3 Target</th></tr></thead><tbody><tr><td>Active users</td><td>500</td><td>2,000</td><td>5,000</td></tr><tr><td>Service providers onboarded</td><td>50 (10 premium)</td><td>100 (30 premium)</td><td>200 (75 premium)</td></tr><tr><td>Rewards redeemed</td><td>300</td><td>4,000</td><td>15,000</td></tr><tr><td>Borough/public contracts</td><td>MoUs signed</td><td>3 pilots live</td><td>6+ boroughs + GLA/NHS</td></tr><tr><td>Sponsor contributions</td><td>£10k value secured</td><td>£60k cumulative</td><td>£150k cumulative</td></tr><tr><td>Support response time</td><td>&lt;24h</td><td>&lt;12h</td><td>&lt;12h</td></tr></tbody></table><h2 id="_6-dependencies-risks" tabindex="-1">6. Dependencies &amp; Risks <a class="header-anchor" href="#_6-dependencies-risks" aria-label="Permalink to &quot;6. Dependencies &amp; Risks&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Dependency</th><th>Mitigation</th></tr></thead><tbody><tr><td>Access to accurate service data feeds</td><td>Establish data-sharing agreements early, fallback manual verification schedule</td></tr><tr><td>Sponsor pipeline for rewards</td><td>Maintain multi-tier pipeline, pre-purchase essential rewards inventory</td></tr><tr><td>Borough procurement timelines</td><td>Align deliverables to budget cycles, offer pilot-friendly contracts</td></tr><tr><td>Technical debt re-emergence</td><td>Enforce architecture guardrails, include observability from day one</td></tr></tbody></table><h2 id="_7-reporting-cadence" tabindex="-1">7. Reporting Cadence <a class="header-anchor" href="#_7-reporting-cadence" aria-label="Permalink to &quot;7. Reporting Cadence&quot;">​</a></h2><ul><li><strong>Weekly:</strong> Rebuild stand-up summary (engineering + product).</li><li><strong>Monthly:</strong> Partner bulletin + public changelog.</li><li><strong>Quarterly:</strong> Board/advisory update including KPIs, risks, funding pipeline.</li><li><strong>Ad hoc:</strong> Incident or milestone reports (e.g., data breach drill, sponsor announcements).</li></ul><h2 id="_8-immediate-next-steps" tabindex="-1">8. Immediate Next Steps <a class="header-anchor" href="#_8-immediate-next-steps" aria-label="Permalink to &quot;8. Immediate Next Steps&quot;">​</a></h2><ol><li>Finalise MVP architecture RFC and circulate to advisors for review.</li><li>Kick off automated service ingest sprint; parallel user research sessions to validate UX flows.</li><li>Draft sponsor packages + secure first LOIs before rewards rebuild completes.</li><li>Prepare grant application packs with refreshed market data and KPIs.</li></ol><hr><p><em>Owners: Product Lead (overall), Partnerships Manager (workstreams 3.2/3.4), Community Lead (support).</em></p>',28)])])}const m=e(n,[["render",i]]);export{u as __pageData,m as default};
