import{_ as i,c as a,o as t,ag as r}from"./chunks/framework._dqZt2de.js";const p=JSON.parse('{"title":"HelpmApp Pitch Outline (Post-Reset)","description":"","frontmatter":{},"headers":[],"relativePath":"archive/pitch_outline_draft.md","filePath":"archive/pitch_outline_draft.md"}'),o={name:"archive/pitch_outline_draft.md"};function n(l,e,s,d,c,u){return t(),a("div",null,[...e[0]||(e[0]=[r('<h1 id="helpmapp-pitch-outline-post-reset" tabindex="-1">HelpmApp Pitch Outline (Post-Reset) <a class="header-anchor" href="#helpmapp-pitch-outline-post-reset" aria-label="Permalink to &quot;HelpmApp Pitch Outline (Post-Reset)&quot;">​</a></h1><p><strong>Version:</strong> 1.0<br><strong>Last Updated:</strong> 17 Nov 2025</p><hr><h2 id="_1-title-slide" tabindex="-1">1. Title Slide <a class="header-anchor" href="#_1-title-slide" aria-label="Permalink to &quot;1. Title Slide&quot;">​</a></h2><ul><li>HelpmApp: Connecting the Homeless with Services Through Gamified Support</li><li>Tagline: Find Help. Earn Rewards. Build Your Future.</li><li>Status: Clean-slate rebuild underway (code/infrastructure reset).</li></ul><h2 id="_2-problem" tabindex="-1">2. Problem <a class="header-anchor" href="#_2-problem" aria-label="Permalink to &quot;2. Problem&quot;">​</a></h2><ul><li>13,231 people slept rough in London in 2024/25 (record high).</li><li>Fragmented 10,000+ service slots; outdated directories waste time and money.</li><li>Providers lack real-time data; boroughs can’t monitor utilisation or gaps.</li><li>Digital divide: low-end devices, intermittent connectivity, low engagement.</li></ul><h2 id="_3-solution" tabindex="-1">3. Solution <a class="header-anchor" href="#_3-solution" aria-label="Permalink to &quot;3. Solution&quot;">​</a></h2><ul><li>Mobile-first PWA (native apps upcoming) with verified service listings + offline cache.</li><li>Gamified check-ins earn stars redeemable for food, hygiene, transport, phone credit.</li><li>Provider and borough dashboards offer anonymised utilisation insights.</li><li>Lived-experience governance ensures dignity, privacy, and relevance.</li></ul><h2 id="_4-product-differentiators" tabindex="-1">4. Product Differentiators <a class="header-anchor" href="#_4-product-differentiators" aria-label="Permalink to &quot;4. Product Differentiators&quot;">​</a></h2><ul><li>Pokémon Go-style engagement + rewards to drive sustained use.</li><li>Real-time availability (open/closed, capacity signals, community feedback).</li><li>Automated data ingest + validation pipeline.</li><li>Impact instrumentation baked in (check-ins, redemptions, geography, KPIs).</li></ul><h2 id="_5-traction-readiness" tabindex="-1">5. Traction &amp; Readiness <a class="header-anchor" href="#_5-traction-readiness" aria-label="Permalink to &quot;5. Traction &amp; Readiness&quot;">​</a></h2><ul><li>Previous PWA prototype live with 64+ services (now archived).</li><li>Partnerships in conversation: St Mungo’s, The Passage, Westminster, Camden, Lambeth.</li><li>Community: Telegram channel + peer advocate model ready for relaunch.</li><li>Reset gives opportunity to rebuild architecture + governance based on learnings.</li></ul><h2 id="_6-roadmap-18-months" tabindex="-1">6. Roadmap (18 Months) <a class="header-anchor" href="#_6-roadmap-18-months" aria-label="Permalink to &quot;6. Roadmap (18 Months)&quot;">​</a></h2><ol><li><strong>Foundation (M1-6):</strong> Rebuild MVP, 500 users, 50 providers, £10k rewards value.</li><li><strong>Growth (M7-12):</strong> London-wide expansion, dashboards v1, 2k users, 3 borough pilots.</li><li><strong>Scale (M13-18):</strong> Manchester/Birmingham, native apps, API, 5k users, 200 providers.</li></ol><h2 id="_7-market-business-model" tabindex="-1">7. Market &amp; Business Model <a class="header-anchor" href="#_7-market-business-model" aria-label="Permalink to &quot;7. Market &amp; Business Model&quot;">​</a></h2><ul><li>Target users: people experiencing homelessness (primary), service providers, boroughs, corporate sponsors.</li><li>Revenue mix: provider subscriptions (£100-150/mo), borough/GLA contracts (£30-60k per authority), corporate sponsorship (cash + in-kind), grants (bridge).</li></ul><h2 id="_8-impact-metrics" tabindex="-1">8. Impact Metrics <a class="header-anchor" href="#_8-impact-metrics" aria-label="Permalink to &quot;8. Impact Metrics&quot;">​</a></h2><ul><li>User outcomes: discovery time &lt;2 min, 4+ check-ins/user/month, 60% rewards redemption.</li><li>System impact: 95% listing completeness, provider dashboard adoption, borough pilots.</li><li>Stakeholder value: sponsor renewal %, case studies, contract retention.</li></ul><h2 id="_9-funding-ask" tabindex="-1">9. Funding Ask <a class="header-anchor" href="#_9-funding-ask" aria-label="Permalink to &quot;9. Funding Ask&quot;">​</a></h2><ul><li><strong>£70,000</strong> for Year 1 operations (rebuild funded separately).</li><li>Breakdown: £25k marketing, £20k rewards pool, £10k partnerships, £15k ops.</li><li>Path to sustainability by Year 3 (projected £635k revenue, £555k expenses, £80k surplus).</li></ul><h2 id="_10-team-governance" tabindex="-1">10. Team &amp; Governance <a class="header-anchor" href="#_10-team-governance" aria-label="Permalink to &quot;10. Team &amp; Governance&quot;">​</a></h2><ul><li>Founders: product/engineering + lived-experience leadership.</li><li>Advisory board: representatives from major homelessness charities, borough housing leads, technologists, and individuals with lived experience.</li><li>Peer advocate program and monthly governance reviews.</li></ul><h2 id="_11-call-to-action" tabindex="-1">11. Call to Action <a class="header-anchor" href="#_11-call-to-action" aria-label="Permalink to &quot;11. Call to Action&quot;">​</a></h2><ul><li>Funders: support £70k working capital (UnLtd, DSIT, Lottery, CSR, angels).</li><li>Service providers/boroughs: join pilot cohorts for data-driven insights.</li><li>Sponsors: underwrite rewards (food, hygiene, travel, connectivity).</li></ul><hr><p><em>Use this outline to rebuild the slide deck or present verbally; figures aligned with Version 2.0 documentation.</em></p>',27)])])}const m=i(o,[["render",n]]);export{p as __pageData,m as default};
