import{_ as t,c as r,o as i,ag as a}from"./chunks/framework._dqZt2de.js";const u=JSON.parse('{"title":"HelpmApp Quick Reference Guide (Reset Edition)","description":"","frontmatter":{},"headers":[],"relativePath":"archive/quick_ref_legacy.md","filePath":"archive/quick_ref_legacy.md"}'),o={name:"archive/quick_ref_legacy.md"};function n(s,e,l,d,h,p){return i(),r("div",null,[...e[0]||(e[0]=[a('<h1 id="helpmapp-quick-reference-guide-reset-edition" tabindex="-1">HelpmApp Quick Reference Guide (Reset Edition) <a class="header-anchor" href="#helpmapp-quick-reference-guide-reset-edition" aria-label="Permalink to &quot;HelpmApp Quick Reference Guide (Reset Edition)&quot;">​</a></h1><p><strong>Date:</strong> 17 Nov 2025<br><strong>Status:</strong> Rebuild in progress (code + infra wiped to reassess strategy)</p><hr><h2 id="opportunity-snapshot" tabindex="-1">Opportunity Snapshot <a class="header-anchor" href="#opportunity-snapshot" aria-label="Permalink to &quot;Opportunity Snapshot&quot;">​</a></h2><ul><li><strong>13,231</strong> people currently sleeping rough in London (record high).</li><li><strong>10,000+</strong> service slots across London create fragmentation and information overload.</li><li>Smartphone access among unhoused Londoners now <strong>60-70%</strong>, but tools are not optimised for low-end devices.</li><li>HelpmApp fills the gap with <strong>real-time service discovery + gamified rewards + actionable data</strong> for providers and policymakers.</li></ul><h2 id="what-s-new-post-reset" tabindex="-1">What’s New Post-Reset <a class="header-anchor" href="#what-s-new-post-reset" aria-label="Permalink to &quot;What’s New Post-Reset&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Area</th><th>Previous State</th><th>Reset Update</th></tr></thead><tbody><tr><td>Product</td><td>Live PWA with manual service updates</td><td>Rebuilding with automated data ingest, offline-first architecture, native app path</td></tr><tr><td>Infrastructure</td><td>Vercel + MongoDB + Neon/Supabase mix</td><td>Replatform decision pending (Next.js/Expo + PlanetScale/Supabase + edge caching)</td></tr><tr><td>Data</td><td>64 services manually curated</td><td>Automated import of London Atlas + manual verification workflows</td></tr><tr><td>Rewards</td><td>Prototype catalogue</td><td>Formal sponsor pipeline targeting £10k cash + £10k in-kind pre-launch</td></tr><tr><td>Governance</td><td>Informal advisory loop</td><td>Codified board with lived-experience reps, privacy reviews each quarter</td></tr></tbody></table><h2 id="product-pillars" tabindex="-1">Product Pillars <a class="header-anchor" href="#product-pillars" aria-label="Permalink to &quot;Product Pillars&quot;">​</a></h2><ol><li><strong>Service Discovery:</strong> Verified listings with hours, capacity signals, filters, map/list, offline cache.</li><li><strong>Gamified Engagement:</strong> Stars for check-ins, streaks, referrals; redeemable for food, hygiene, travel, phone credit.</li><li><strong>Insight Dashboards:</strong> Providers/local authorities receive utilisation trends, gaps, and co-utilisation maps.</li><li><strong>Community &amp; Support:</strong> Peer advocates, Telegram community, human support under 12h response time.</li></ol><h2 id="_18-month-targets" tabindex="-1">18-Month Targets <a class="header-anchor" href="#_18-month-targets" aria-label="Permalink to &quot;18-Month Targets&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Milestone</th><th>Month 6</th><th>Month 12</th><th>Month 18</th></tr></thead><tbody><tr><td>Active Users</td><td>500</td><td>2,000</td><td>5,000</td></tr><tr><td>Service Providers</td><td>50 (10 premium)</td><td>100 (30 premium)</td><td>200 (75 premium)</td></tr><tr><td>Check-ins (cumulative)</td><td>10,000</td><td>120,000</td><td>360,000</td></tr><tr><td>Rewards Redeemed</td><td>300</td><td>4,000</td><td>15,000</td></tr><tr><td>Borough/Public Contracts</td><td>MoUs signed</td><td>3 pilots live</td><td>6 boroughs + GLA/NHS</td></tr><tr><td>Sponsor Value</td><td>£10k cash + £10k in-kind</td><td>£60k cumulative</td><td>£150k cumulative</td></tr></tbody></table><h2 id="funding-requirement" tabindex="-1">Funding Requirement <a class="header-anchor" href="#funding-requirement" aria-label="Permalink to &quot;Funding Requirement&quot;">​</a></h2><p><strong>£70,000</strong> working capital for Year 1 operations (product rebuild funded separately).</p><ul><li>Marketing &amp; User Acquisition: £25k</li><li>Rewards Pool: £20k</li><li>Partnerships &amp; BD: £10k</li><li>Operations &amp; Support: £15k</li></ul><p><strong>Capital Stack Targets:</strong></p><ol><li>UnLtd Scaling Up Grant (£10-15k) – Submitted Q4 2025</li><li>Digital Inclusion Innovation Fund (£25-40k) – Submission in progress</li><li>National Lottery Partnerships (£20-30k multi-year) – Drafting Q1 2026</li><li>Corporate CSR bundles / angel bridge (£10-20k) if needed</li></ol><h2 id="competitive-edge" tabindex="-1">Competitive Edge <a class="header-anchor" href="#competitive-edge" aria-label="Permalink to &quot;Competitive Edge&quot;">​</a></h2><ul><li><strong>Only UK platform</strong> combining gamified engagement + real-time availability + data insights for homelessness services.</li><li><strong>Low-bandwidth design</strong> for low-spec phones, offline browsing, minimal data usage.</li><li><strong>Data network effects</strong>: more check-ins → better insights → more partners → richer rewards.</li><li><strong>Lived-experience governance</strong> ensures relevance and ethical data use.</li></ul><h2 id="immediate-next-steps" tabindex="-1">Immediate Next Steps <a class="header-anchor" href="#immediate-next-steps" aria-label="Permalink to &quot;Immediate Next Steps&quot;">​</a></h2><ol><li>Finalise architecture RFC + rebuild schedule (Month 1).</li><li>Automate service import + verification tooling (Months 1-2).</li><li>Lock sponsor LOIs worth £10k cash + £10k in-kind before MVP relaunch (Month 3).</li><li>Run co-design sessions with users/providers to validate revised UX (Months 2-4).</li><li>Submit grant applications (UnLtd, Digital Inclusion Fund) with updated metrics.</li></ol><h2 id="contact-links" tabindex="-1">Contact &amp; Links <a class="header-anchor" href="#contact-links" aria-label="Permalink to &quot;Contact &amp; Links&quot;">​</a></h2><ul><li><strong>Website (legacy):</strong> <a href="https://www.helpmapp.org" target="_blank" rel="noreferrer">https://www.helpmapp.org</a> (offline during rebuild)</li><li><strong>Telegram:</strong> <a href="https://t.me/helpmappuk" target="_blank" rel="noreferrer">https://t.me/helpmappuk</a></li><li><strong>Email:</strong> <a href="mailto:hello@helpmapp.org" target="_blank" rel="noreferrer">hello@helpmapp.org</a></li></ul><hr><p><em>Use this one-pager when approaching partners, funders, or internal stakeholders who need the high-level story quickly. Refer them to the Executive Summary + Playbooks for depth.</em></p>',24)])])}const g=t(o,[["render",n]]);export{u as __pageData,g as default};
