import{_ as a}from"./chunks/dashboard-intelligence.6xDNHw3l.js";import{_ as i,c as t,o as r,ag as o}from"./chunks/framework._dqZt2de.js";const g=JSON.parse('{"title":"Analytics & Intelligence","description":"","frontmatter":{},"headers":[],"relativePath":"guide/provider/analytics.md","filePath":"guide/provider/analytics.md"}'),s={name:"guide/provider/analytics.md"};function n(l,e,c,d,p,h){return r(),t("div",null,[...e[0]||(e[0]=[o('<h1 id="analytics-intelligence" tabindex="-1">Analytics &amp; Intelligence <a class="header-anchor" href="#analytics-intelligence" aria-label="Permalink to &quot;Analytics &amp; Intelligence&quot;">​</a></h1><p>HelpmApp transforms raw engagement data into actionable intelligence for your organization. Understanding your impact is the first step toward optimization.</p><h2 id="core-metrics" tabindex="-1">Core Metrics <a class="header-anchor" href="#core-metrics" aria-label="Permalink to &quot;Core Metrics&quot;">​</a></h2><p>We track three primary indicators of service performance:</p><ul><li><strong>Impressions</strong>: How many times your service profile was viewed by a user.</li><li><strong>Interactions</strong>: Clicks on your phone number, email, or website (intent to contact).</li><li><strong>Check-ins</strong>: Validated user visits to your location via QR or geofence.</li></ul><p><img src="'+a+'" alt="HelpmApp Provider Intelligence"></p><h2 id="strategic-insights" tabindex="-1">Strategic Insights <a class="header-anchor" href="#strategic-insights" aria-label="Permalink to &quot;Strategic Insights&quot;">​</a></h2><h3 id="conversion-funnel" tabindex="-1">Conversion Funnel <a class="header-anchor" href="#conversion-funnel" aria-label="Permalink to &quot;Conversion Funnel&quot;">​</a></h3><p>Visualize the user journey from initial discovery to validated visit. Our dashboard calculates the conversion rate to help you understand if your service details effectively guide users to your door.</p><h3 id="peak-engagement-hours" tabindex="-1">Peak Engagement Hours <a class="header-anchor" href="#peak-engagement-hours" aria-label="Permalink to &quot;Peak Engagement Hours&quot;">​</a></h3><p>Our 7x24 matrix analyzes when users are most active with your listings. This data helps you optimize staffing and ensure support is available when demand is highest.</p><h3 id="gap-analysis" tabindex="-1">Gap Analysis <a class="header-anchor" href="#gap-analysis" aria-label="Permalink to &quot;Gap Analysis&quot;">​</a></h3><p>Contrasts official MHCLG rough sleeping data with platform service density. This allows you to identify &quot;Service Deserts&quot; where your organization&#39;s expansion could have the highest social impact.</p><h2 id="impact-reports" tabindex="-1">Impact Reports <a class="header-anchor" href="#impact-reports" aria-label="Permalink to &quot;Impact Reports&quot;">​</a></h2><p>Generate grant-ready professional reports in PDF format directly from your dashboard. These reports include:</p><ul><li>High-level KPI summaries.</li><li>Monthly performance narratives.</li><li>Comparative benchmarking against peer services in your category.</li></ul><h3 id="mobile-access" tabindex="-1">Mobile Access <a class="header-anchor" href="#mobile-access" aria-label="Permalink to &quot;Mobile Access&quot;">​</a></h3><p>Manage your services on the go. The unified provider navigation ensures you have instant access to your <strong>Dashboard</strong> and <strong>Team</strong> management from any mobile device. (See <a href="/roadmap/">Project Roadmap</a> for upcoming features).</p><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p><strong>Privacy First</strong>: All analytics are strictly aggregated and anonymized. We never share individual user data with providers.</p></div>',19)])])}const y=i(s,[["render",n]]);export{g as __pageData,y as default};
