import{_ as t,c as a,o as r,ag as i}from"./chunks/framework.CAz5-5wf.js";const o="/images/home_public.png",s="/images/search_results.png",m=JSON.parse('{"title":"Getting Started with HelpmApp","description":"","frontmatter":{},"headers":[],"relativePath":"guide/user/getting-started.md","filePath":"guide/user/getting-started.md"}'),n={name:"guide/user/getting-started.md"};function l(p,e,c,d,h,g){return r(),a("div",null,[...e[0]||(e[0]=[i('<h1 id="getting-started-with-helpmapp" tabindex="-1">Getting Started with HelpmApp <a class="header-anchor" href="#getting-started-with-helpmapp" aria-label="Permalink to &quot;Getting Started with HelpmApp&quot;">​</a></h1><p>HelpmApp is a mission-driven platform designed to connect people experiencing homelessness in London with critical support services. We believe that access to information is a fundamental right.</p><h2 id="how-it-works" tabindex="-1">How It Works <a class="header-anchor" href="#how-it-works" aria-label="Permalink to &quot;How It Works&quot;">​</a></h2><p>HelpmApp aggregates real-time data from local councils, charities, and community service providers. Our platform verifies this information and presents it in an accessible, offline-first format.</p><h3 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to &quot;Key Features&quot;">​</a></h3><p><img src="'+o+'" alt="HelpmApp Homepage"></p><ul><li><strong>Find Nearby</strong>: Use our geolocation features to find the closest help to your current location. The map automatically updates to show services around you.</li><li><strong>Offline Access</strong>: The app is designed to work even when you lose your data connection. Once loaded, you can search and view service details without internet.</li></ul><p><img src="'+s+'" alt="Search Results"></p><ul><li><strong>Up-to-Date Information</strong>: We receive regular updates from our network of providers to ensure that opening times, services offered, and eligibility criteria are accurate.</li></ul><h2 id="using-the-map" tabindex="-1">Using the Map <a class="header-anchor" href="#using-the-map" aria-label="Permalink to &quot;Using the Map&quot;">​</a></h2><ol><li><strong>Allow Location Access</strong>: When prompted, allow HelpmApp to access your location for the best experience.</li><li><strong>Browse Services</strong>: Pins on the map represent different services like Food Banks, Shelters, and Healthcare.</li><li><strong>Filter</strong>: Use the search bar or filter buttons to narrow down services by category or borough.</li><li><strong>View Details</strong>: Tap on a pin or a list item to see full details, including directions and contact information.</li></ol>',11)])])}const f=t(n,[["render",l]]);export{m as __pageData,f as default};
