import{_ as t,a as i,b as a}from"./chunks/4_sticky_header_scroll.b5TDb52O.js";import{_ as o,c as r,o as s,ag as n}from"./chunks/framework._dqZt2de.js";const y=JSON.parse('{"title":"Getting Started with HelpmApp","description":"","frontmatter":{},"headers":[],"relativePath":"guide/user/getting-started.md","filePath":"guide/user/getting-started.md"}'),l={name:"guide/user/getting-started.md"};function c(p,e,h,d,m,u){return s(),r("div",null,[...e[0]||(e[0]=[n('<h1 id="getting-started-with-helpmapp" tabindex="-1">Getting Started with HelpmApp <a class="header-anchor" href="#getting-started-with-helpmapp" aria-label="Permalink to &quot;Getting Started with HelpmApp&quot;">​</a></h1><p>HelpmApp is a mission-driven platform designed to connect people experiencing homelessness in London with critical support services. We believe that access to information is a fundamental right.</p><h2 id="how-it-works" tabindex="-1">How It Works <a class="header-anchor" href="#how-it-works" aria-label="Permalink to &quot;How It Works&quot;">​</a></h2><p>HelpmApp aggregates real-time data from local councils, charities, and community service providers. Our platform verifies this information and presents it in an accessible, offline-first format.</p><h3 id="key-features" tabindex="-1">Key Features <a class="header-anchor" href="#key-features" aria-label="Permalink to &quot;Key Features&quot;">​</a></h3><p><img src="'+t+'" alt="Mobile Discovery"></p><ul><li><strong>Semantic Omnibar</strong>: We&#39;ve combined search and filters into a single, intuitive &quot;Omnibar&quot; at the top of the map. No more hunting for buttons—just tap and type.</li><li><strong>Instant &quot;Open Now&quot; Toggle</strong>: Need help immediately? Tap the clock icon in the search bar to instantly hide any services that are currently closed.</li><li><strong>Offline Access</strong>: Our PWA (Progressive Web App) technology ensures your data stays with you. Once you&#39;ve visited HelpmApp, essential service data remains available even if you lose your internet connection.</li></ul><p><img src="'+i+'" alt="Search &amp; Filter Experience"></p><ul><li><strong>Up-to-Date Information</strong>: Our &quot;Tier-1&quot; verified data coming from the helpmapp.org production engine ensures you have the most accurate opening times and eligibility details.</li></ul><h2 id="using-the-platform" tabindex="-1">Using the Platform <a class="header-anchor" href="#using-the-platform" aria-label="Permalink to &quot;Using the Platform&quot;">​</a></h2><ol><li><strong>Allow Location Access</strong>: When prompted, allow HelpmApp to access your location. This enables the map to prioritize services closest to you.</li><li><strong>The Omnibar</strong>: Tap the search bar to find specific services by name, or tap the filter icon inside it to browse by category (Food, Healthcare, Shelter, etc.).</li><li><strong>Sticky Context</strong>: As you scroll through the list of services, a sticky bar at the top keeps you informed of how many services match your current filters.</li><li><strong>View Details</strong>: Tap on any service card or map pin to see full details, including a direct &quot;Call&quot; button and navigation links.</li></ol><p><img src="'+a+'" alt="Sticky Results Feedback"></p>',12)])])}const b=o(l,[["render",c]]);export{y as __pageData,b as default};
