import{_ as e,c as r,o as a,ag as n}from"./chunks/framework._dqZt2de.js";const u=JSON.parse('{"title":"HelpmApp Executive Summary","description":"","frontmatter":{},"headers":[],"relativePath":"knowledge-base/internal/EXECUTIVE_SUMMARY.md","filePath":"knowledge-base/internal/EXECUTIVE_SUMMARY.md"}'),i={name:"knowledge-base/internal/EXECUTIVE_SUMMARY.md"};function o(s,t,l,g,d,p){return a(),r("div",null,[...t[0]||(t[0]=[n('<h1 id="helpmapp-executive-summary" tabindex="-1">HelpmApp Executive Summary <a class="header-anchor" href="#helpmapp-executive-summary" aria-label="Permalink to &quot;HelpmApp Executive Summary&quot;">​</a></h1><p><strong>Version:</strong> 3.5<br><strong>Last Updated:</strong> February 17, 2026<br><strong>Status:</strong> Live in Production, CIC Registration In-Progress (Phase 74 Complete)</p><hr><h2 id="_1-current-status-strategic-pivot" tabindex="-1">1. Current Status &amp; Strategic Pivot <a class="header-anchor" href="#_1-current-status-strategic-pivot" aria-label="Permalink to &quot;1. Current Status &amp; Strategic Pivot&quot;">​</a></h2><p>HelpmApp is a <strong>live, production-ready Progressive Web App (PWA)</strong> at <a href="https://www.helpmapp.org" target="_blank" rel="noreferrer">www.helpmapp.org</a>, providing real-time data on over <strong>3,500+ services</strong> across every region of England. The platform has successfully transitioned from regional pilots to national scale, featuring ex-FAANG grade intelligence dashboards for providers and a robust, AI-driven data remediation pipeline.</p><p><img src="https://helpmapp.github.io/images/home_public.png" alt="HelpmApp Homepage"><em>Figure 1: The live HelpmApp homepage, showing 3,500+ services across the UK.</em></p><h2 id="_2-problem-validated-solution" tabindex="-1">2. Problem &amp; Validated Solution <a class="header-anchor" href="#_2-problem-validated-solution" aria-label="Permalink to &quot;2. Problem &amp; Validated Solution&quot;">​</a></h2><ul><li><strong>The Problem:</strong> London&#39;s <strong>13,000+</strong> rough sleepers struggle to navigate a fragmented ecosystem of <strong>10,000+</strong> support services. Information is opaque, leading to wasted time and missed opportunities for critical aid.</li><li><strong>The Solution:</strong> HelpmApp provides a single, user-friendly interface to find and access verified services. Its gamified rewards system incentivizes engagement, while the underlying data engine provides invaluable insights for providers and policymakers.</li></ul><h2 id="_3-live-traction-key-metrics-as-of-dec-2025" tabindex="-1">3. Live Traction &amp; Key Metrics (as of Dec 2025) <a class="header-anchor" href="#_3-live-traction-key-metrics-as-of-dec-2025" aria-label="Permalink to &quot;3. Live Traction &amp; Key Metrics (as of Dec 2025)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Metric</th><th style="text-align:left;">Current Value</th><th style="text-align:left;">Notes</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Live Services</strong></td><td style="text-align:left;">3,527+</td><td style="text-align:left;">100% enriched via Hermes AI Pipeline (Phase 73).</td></tr><tr><td style="text-align:left;"><strong>Regional Coverage</strong></td><td style="text-align:left;">UK-wide</td><td style="text-align:left;">Coverage in 100% of England; expanding in Devolved Nations.</td></tr><tr><td style="text-align:left;"><strong>Rewards System</strong></td><td style="text-align:left;">Fully Functional</td><td style="text-align:left;">5 reward types available, sponsored by partners like TfL.</td></tr><tr><td style="text-align:left;"><strong>User Base</strong></td><td style="text-align:left;">Pre-launch</td><td style="text-align:left;">Ready for targeted user acquisition campaigns.</td></tr></tbody></table><p><img src="https://i.imgur.com/your_screenshot_url_here.png" alt="HelpmApp Rewards Catalogue"><em>Figure 2: The fully functional Rewards Catalogue, a core component of the gamified engagement strategy.</em></p><h2 id="_4-12-month-execution-plan-post-funding" tabindex="-1">4. 12-Month Execution Plan (Post-Funding) <a class="header-anchor" href="#_4-12-month-execution-plan-post-funding" aria-label="Permalink to &quot;4. 12-Month Execution Plan (Post-Funding)&quot;">​</a></h2><ol><li><strong>Phase 1 – Market Entry (Months 1-4)</strong><ul><li>Secure CIC status and initial grant funding (£70k target).</li><li>Launch targeted user acquisition campaigns in Camden, Westminster, and Lambeth, aiming for <strong>500 active users</strong>.</li><li>Onboard <strong>50 service providers</strong> and formalize data-sharing agreements.</li></ul></li><li><strong>Phase 2 – Expansion &amp; Impact (Months 5-12)</strong><ul><li>Expand user acquisition across all 32 UK regions and boroughs, targeting <strong>2,000 active users</strong>.</li><li>Launch the Provider Portal and secure <strong>3 borough-level data-sharing pilots</strong>.</li><li>Grow the rewards program with <strong>7+ corporate sponsors</strong>.</li></ul></li></ol><h2 id="_5-funding-requirement-£70-000-for-direct-impact" tabindex="-1">5. Funding Requirement: £70,000 for Direct Impact <a class="header-anchor" href="#_5-funding-requirement-£70-000-for-direct-impact" aria-label="Permalink to &quot;5. Funding Requirement: £70,000 for Direct Impact&quot;">​</a></h2><p><strong>Ask:</strong> £70,000 to fund the first 12 months of market execution. <strong>Crucially, no funds are required for development.</strong></p><table tabindex="0"><thead><tr><th style="text-align:left;">Use of Funds</th><th style="text-align:left;">Amount</th><th style="text-align:left;">Impact Focus</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Rewards Pool</strong></td><td style="text-align:left;">£30,000</td><td style="text-align:left;">Directly funds the incentives for users to engage with services.</td></tr><tr><td style="text-align:left;"><strong>User Acquisition &amp; Outreach</strong></td><td style="text-align:left;">£25,000</td><td style="text-align:left;">Peer advocate programs, community events, and materials.</td></tr><tr><td style="text-align:left;"><strong>Partnership Development</strong></td><td style="text-align:left;">£15,000</td><td style="text-align:left;">Onboarding service providers and securing corporate sponsorships.</td></tr><tr><td style="text-align:left;"><strong>Total</strong></td><td style="text-align:left;"><strong>£70,000</strong></td><td style="text-align:left;"></td></tr></tbody></table><p><strong>Capital Efficiency:</strong> The &quot;Zero-Cost Infrastructure&quot; (Vercel, Supabase) makes this one of the most capital-efficient social impact investments available.</p><h2 id="_6-next-steps" tabindex="-1">6. Next Steps <a class="header-anchor" href="#_6-next-steps" aria-label="Permalink to &quot;6. Next Steps&quot;">​</a></h2><ol><li><strong>Finalize CIC Incorporation:</strong> Complete and file Form CIC36.</li><li><strong>Submit Priority Grant Applications:</strong> Target Bethnal Green Ventures (Jan 4 deadline) and the London Homelessness Foundation.</li><li><strong>Provider Registration:</strong> The 404 error on <code>/provider/register</code> has been <strong>FIXED</strong>.</li><li><strong>Launch User Acquisition Pilot:</strong> Begin outreach in the initial three target boroughs.</li></ol><hr><p><strong>Contact</strong></p><ul><li><strong>Website:</strong> <a href="https://www.helpmapp.org" target="_blank" rel="noreferrer">https://www.helpmapp.org</a></li><li><strong>Email:</strong> <a href="mailto:helpmapp@proton.me" target="_blank" rel="noreferrer">helpmapp@proton.me</a></li></ul>',22)])])}const h=e(i,[["render",o]]);export{u as __pageData,h as default};
