import{_ as t,c as a,o as r,ag as i}from"./chunks/framework._dqZt2de.js";const h=JSON.parse('{"title":"HelpmApp Impact Measurement Framework (Reset Edition)","description":"","frontmatter":{},"headers":[],"relativePath":"knowledge-base/internal/IMPACT_FRAMEWORK.md","filePath":"knowledge-base/internal/IMPACT_FRAMEWORK.md"}'),o={name:"knowledge-base/internal/IMPACT_FRAMEWORK.md"};function s(n,e,d,l,c,p){return r(),a("div",null,[...e[0]||(e[0]=[i('<h1 id="helpmapp-impact-measurement-framework-reset-edition" tabindex="-1">HelpmApp Impact Measurement Framework (Reset Edition) <a class="header-anchor" href="#helpmapp-impact-measurement-framework-reset-edition" aria-label="Permalink to &quot;HelpmApp Impact Measurement Framework (Reset Edition)&quot;">​</a></h1><p><strong>Version:</strong> 2.0<br><strong>Last Updated:</strong> 17 Nov 2025<br><strong>Purpose:</strong> Define how we evidence impact during and after the rebuild, ensuring funders, partners, and lived-experience stakeholders can verify outcomes.</p><hr><h2 id="_1-impact-principles" tabindex="-1">1. Impact Principles <a class="header-anchor" href="#_1-impact-principles" aria-label="Permalink to &quot;1. Impact Principles&quot;">​</a></h2><ol><li><strong>Do no harm:</strong> Prioritise consent, privacy, and data minimisation from day one of the rebuild.</li><li><strong>Evidence what matters:</strong> Focus on metrics that correlate directly with improved access to services and system efficiency.</li><li><strong>Triangulate:</strong> Combine quantitative analytics, qualitative stories, and partner validation.</li><li><strong>Continuous feedback:</strong> Use data to iterate product and operations, not just to report externally.</li></ol><h2 id="_2-impact-domains-kpis" tabindex="-1">2. Impact Domains &amp; KPIs <a class="header-anchor" href="#_2-impact-domains-kpis" aria-label="Permalink to &quot;2. Impact Domains &amp; KPIs&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Domain</th><th>Primary KPIs</th><th>Measurement Method</th><th>Targets</th></tr></thead><tbody><tr><td><strong>User Outcomes</strong></td><td>Active users, check-ins per user, service discovery time, reward value delivered, NPS, issue resolution time</td><td>App analytics, timed usability tests, in-app surveys, support logs</td><td>Phase 1: 500 users @4+ check-ins, &lt;2 min discovery, £10k rewards redeemed, NPS 50+</td></tr><tr><td><strong>System Impact</strong></td><td>Services listed/completeness, provider dashboard usage, geographic coverage, co-utilisation insights, borough pilot count</td><td>Service DB audits, dashboard telemetry, GIS heatmaps, quarterly partner reports</td><td>95% listing completeness, 50 providers active on dashboards, 3 borough pilots by Month 12</td></tr><tr><td><strong>Stakeholder Value</strong></td><td>Provider satisfaction, contract renewals, sponsor retention, impact cases published</td><td>Surveys, renewal rates, sponsor reports, case-study tracker</td><td>80% provider satisfaction, &gt;85% sponsor renewal, 6 case studies/year</td></tr><tr><td><strong>Organisational Health</strong></td><td>Governance meetings held, data privacy compliance, grant reporting timeliness</td><td>Board minutes, DPIA logs, grants tracker</td><td>100% on-time reporting, quarterly DPIA review</td></tr></tbody></table><h2 id="_3-data-sources-tools" tabindex="-1">3. Data Sources &amp; Tools <a class="header-anchor" href="#_3-data-sources-tools" aria-label="Permalink to &quot;3. Data Sources &amp; Tools&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Source</th><th>Usage</th></tr></thead><tbody><tr><td>App telemetry (Mixpanel / custom)</td><td>Active users, session flows, check-ins, rewards, retention</td></tr><tr><td>Service provider portal logs</td><td>Update frequency, dashboard views, feature adoption</td></tr><tr><td>Rewards system ledger</td><td>Redemption patterns, sponsor attribution</td></tr><tr><td>Support &amp; community tools (Telegram, helpdesk)</td><td>Resolution SLAs, sentiment</td></tr><tr><td>Surveys (Typeform)</td><td>NPS, qualitative feedback, demographic opt-in</td></tr><tr><td>Stakeholder interviews</td><td>Case studies, policy impact, qualitative insights</td></tr></tbody></table><h2 id="_4-data-governance" tabindex="-1">4. Data Governance <a class="header-anchor" href="#_4-data-governance" aria-label="Permalink to &quot;4. Data Governance&quot;">​</a></h2><ul><li><strong>Consent:</strong> Simplified onboarding explaining anonymous analytics + optional surveys; explicit opt-in for sharing testimonials.</li><li><strong>Anonymisation:</strong> Check-ins stored without PII; location data aggregated before sharing externally.</li><li><strong>Access control:</strong> Role-based permissions; borough dashboards show aggregated, not individual, data.</li><li><strong>Retention:</strong> Raw check-in logs retained 24 months (or shorter if required); aggregated metrics kept for longitudinal analysis.</li><li><strong>Audit:</strong> Quarterly privacy review with advisory board (including lived-experience reps).</li></ul><h2 id="_5-reporting-cadence" tabindex="-1">5. Reporting Cadence <a class="header-anchor" href="#_5-reporting-cadence" aria-label="Permalink to &quot;5. Reporting Cadence&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Audience</th><th>Frequency</th><th>Artefacts</th></tr></thead><tbody><tr><td>Internal team</td><td>Monthly</td><td>Impact dashboard snapshot + action items</td></tr><tr><td>Advisory board</td><td>Quarterly</td><td>KPI pack, privacy review, narrative summary</td></tr><tr><td>Funders &amp; sponsors</td><td>Quarterly / per agreement</td><td>Custom reports highlighting agreed KPIs, case studies</td></tr><tr><td>Public/community</td><td>Twice yearly</td><td>Impact blog posts, infographics</td></tr></tbody></table><h2 id="_6-learning-agenda-post-reset" tabindex="-1">6. Learning Agenda (Post-Reset) <a class="header-anchor" href="#_6-learning-agenda-post-reset" aria-label="Permalink to &quot;6. Learning Agenda (Post-Reset)&quot;">​</a></h2><ol><li><strong>Does gamification meaningfully increase sustained service engagement?</strong> → Compare check-in streak data to prior baseline.</li><li><strong>Which reward categories drive the highest redemption + impact?</strong> → A/B star pricing, track outcomes with partner surveys.</li><li><strong>How do borough dashboards influence commissioning decisions?</strong> → Document policy references, budget reallocations.</li><li><strong>What barriers remain for offline/on-low-connectivity users?</strong> → Conduct field tests with peer advocates, iterate offline mode.</li></ol><h2 id="_7-implementation-checklist" tabindex="-1">7. Implementation Checklist <a class="header-anchor" href="#_7-implementation-checklist" aria-label="Permalink to &quot;7. Implementation Checklist&quot;">​</a></h2><ul><li>[ ] Instrument rebuilt app with analytics + privacy-safe event schema.</li><li>[ ] Launch opt-in onboarding survey capturing demographics/goals.</li><li>[ ] Stand up impact database / warehouse table for KPI aggregation.</li><li>[ ] Train support + partnerships teams on evidence collection (case studies, testimonials, data requests).</li><li>[ ] Draft data-sharing agreements covering anonymisation + acceptable use for borough partners.</li></ul><h2 id="_8-immediate-next-steps" tabindex="-1">8. Immediate Next Steps <a class="header-anchor" href="#_8-immediate-next-steps" aria-label="Permalink to &quot;8. Immediate Next Steps&quot;">​</a></h2><ol><li>Align engineering + data teams on telemetry requirements before MVP release.</li><li>Prepare version-controlled KPI dashboard (Looker Studio/Metabase) referencing this framework.</li><li>Schedule first quarterly impact review for Month 3 to coincide with MVP pilot feedback.</li></ol><hr><p><em>Contact: <a href="mailto:impact@helpmapp.org" target="_blank" rel="noreferrer">impact@helpmapp.org</a></em></p>',21)])])}const g=t(o,[["render",s]]);export{h as __pageData,g as default};
