import{_ as t,c as a,o as i,ag as s}from"./chunks/framework._dqZt2de.js";const g=JSON.parse('{"title":"HelpmApp Market Analysis 2026","description":"","frontmatter":{},"headers":[],"relativePath":"knowledge-base/internal/MARKET_ANALYSIS_2026.md","filePath":"knowledge-base/internal/MARKET_ANALYSIS_2026.md"}'),r={name:"knowledge-base/internal/MARKET_ANALYSIS_2026.md"};function o(n,e,l,h,u,d){return i(),a("div",null,[...e[0]||(e[0]=[s('<h1 id="helpmapp-market-analysis-2026" tabindex="-1">HelpmApp Market Analysis 2026 <a class="header-anchor" href="#helpmapp-market-analysis-2026" aria-label="Permalink to &quot;HelpmApp Market Analysis 2026&quot;">​</a></h1><p><strong>Date:</strong> January 2026 <strong>Context:</strong> Comprehensive strategic review based on 2025/26 sector data.</p><h2 id="_1-the-problem-scale-evidence-base" tabindex="-1">1. The Problem Scale (Evidence Base) <a class="header-anchor" href="#_1-the-problem-scale-evidence-base" aria-label="Permalink to &quot;1. The Problem Scale (Evidence Base)&quot;">​</a></h2><p>Our &quot;Total Addressable Market&quot; (TAM) is larger than official counts suggest, driven by the &quot;Hidden Homelessness&quot; epidemic.</p><h3 id="_13-231-rough-sleepers-the-visible" tabindex="-1">13,231 Rough Sleepers (The Visible) <a class="header-anchor" href="#_13-231-rough-sleepers-the-visible" aria-label="Permalink to &quot;13,231 Rough Sleepers (The Visible)&quot;">​</a></h3><ul><li><strong>Source:</strong> <a href="./../evidence/chain-report-2025.html">CHAIN Report 2025</a></li><li><strong>Insight:</strong> 63% are <em>new</em> to the streets. This massive churn rate means legacy knowledge (word of mouth) is ineffective. New arrivals need immediate digital discovery.</li></ul><h3 id="_200-000-hidden-homeless-the-invisible" tabindex="-1">200,000+ Hidden Homeless (The Invisible) <a class="header-anchor" href="#_200-000-hidden-homeless-the-invisible" aria-label="Permalink to &quot;200,000+ Hidden Homeless (The Invisible)&quot;">​</a></h3><ul><li><strong>Source:</strong> <a href="./../evidence/hidden-homelessness.html">Crisis Report</a></li><li><strong>Insight:</strong> For every visible rough sleeper, there are ~15 hidden (sofa surfing, cars).</li><li><strong>Opportunity:</strong> These users have smartphones but avoid &quot;homeless services&quot; due to stigma. HelpmApp&#39;s neutral, gamified interface is the bridge for this demographic.</li></ul><h2 id="_2-the-solution-efficacy-why-data-matters" tabindex="-1">2. The Solution Efficacy (Why Data Matters) <a class="header-anchor" href="#_2-the-solution-efficacy-why-data-matters" aria-label="Permalink to &quot;2. The Solution Efficacy (Why Data Matters)&quot;">​</a></h2><p>The &quot;Charity Model&quot; is moving towards &quot;Data-Driven Prevention.&quot;</p><h3 id="the-71-reduction-validator" tabindex="-1">The &quot;71% Reduction&quot; Validator <a class="header-anchor" href="#the-71-reduction-validator" aria-label="Permalink to &quot;The &quot;71% Reduction&quot; Validator&quot;">​</a></h3><ul><li><strong>Source:</strong> <a href="./../evidence/prevention-unit-la.html">LA Policy Lab</a></li><li><strong>Insight:</strong> Predictive targeting reduces homelessness by 71%.</li><li><strong>HelpmApp Strategy:</strong> We are not just a map; we are the <em>data collection engine</em> that makes this prediction possible. Phase 32 (Provider Analytics) is the MVP of this predictive capability.</li></ul><h2 id="_3-funding-landscape-investment-thesis" tabindex="-1">3. Funding Landscape (Investment Thesis) <a class="header-anchor" href="#_3-funding-landscape-investment-thesis" aria-label="Permalink to &quot;3. Funding Landscape (Investment Thesis)&quot;">​</a></h2><p>Capital is shifting from &quot;Emergency Grants&quot; to &quot;Tech For Good Investment.&quot;</p><h3 id="crisis-venture-studio" tabindex="-1">Crisis Venture Studio <a class="header-anchor" href="#crisis-venture-studio" aria-label="Permalink to &quot;Crisis Venture Studio&quot;">​</a></h3><ul><li><strong>Source:</strong> <a href="./../funding/crisis-venture-studio.html">Analysis</a></li><li><strong>Thesis:</strong> &quot;End Homelessness&quot; via scalable tech.</li><li><strong>Match:</strong> HelpmApp&#39;s CIC structure + Tech foundation is a perfect fit.</li></ul><h3 id="bethnal-green-ventures" tabindex="-1">Bethnal Green Ventures <a class="header-anchor" href="#bethnal-green-ventures" aria-label="Permalink to &quot;Bethnal Green Ventures&quot;">​</a></h3><ul><li><strong>Source:</strong> <a href="./../funding/bethnal-green-ventures.html">Analysis</a></li><li><strong>Thesis:</strong> &quot;Tech For Good&quot; (Health/Society).</li><li><strong>Action:</strong> Apply for £60k accelerator using our &quot;Prevention&quot; narrative supported by the LA Policy Lab data.</li></ul><h2 id="_4-strategic-conclusion" tabindex="-1">4. Strategic Conclusion <a class="header-anchor" href="#_4-strategic-conclusion" aria-label="Permalink to &quot;4. Strategic Conclusion&quot;">​</a></h2><p>HelpmApp is uniquely positioned as the <strong>&quot;Digital Infrastructure for Prevention.&quot;</strong></p><ul><li>We serve the <strong>Hidden Homeless</strong> (Consumer App).</li><li>We empower <strong>Data-Driven Policy</strong> (Provider Dashboard).</li><li>We align with <strong>Venture Capital</strong> (Scalable Platform).</li></ul>',21)])])}const p=t(r,[["render",o]]);export{g as __pageData,p as default};
