import{_ as t,c as a,o,ag as r}from"./chunks/framework._dqZt2de.js";const h=JSON.parse('{"title":"Mobile & Ops Delivery Plan","description":"","frontmatter":{},"headers":[],"relativePath":"knowledge-base/internal/MOBILE_DELIVERY_PLAN.md","filePath":"knowledge-base/internal/MOBILE_DELIVERY_PLAN.md"}'),i={name:"knowledge-base/internal/MOBILE_DELIVERY_PLAN.md"};function s(l,e,n,d,c,g){return o(),a("div",null,[...e[0]||(e[0]=[r('<h1 id="mobile-ops-delivery-plan" tabindex="-1">Mobile &amp; Ops Delivery Plan <a class="header-anchor" href="#mobile-ops-delivery-plan" aria-label="Permalink to &quot;Mobile &amp; Ops Delivery Plan&quot;">‚Äã</a></h1><h2 id="_1-operational-intelligence-mcp-setup" tabindex="-1">1. Operational Intelligence (MCP Setup) <a class="header-anchor" href="#_1-operational-intelligence-mcp-setup" aria-label="Permalink to &quot;1. Operational Intelligence (MCP Setup)&quot;">‚Äã</a></h2><p>To support advanced development and mobile operations, we will integrate the following <strong>Model Context Protocol (MCP)</strong> servers.</p><h3 id="recommended-servers" tabindex="-1">Recommended Servers <a class="header-anchor" href="#recommended-servers" aria-label="Permalink to &quot;Recommended Servers&quot;">‚Äã</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">Server</th><th style="text-align:left;">Purpose</th><th style="text-align:left;">Mobile Relevance</th></tr></thead><tbody><tr><td style="text-align:left;"><strong><code>mcp-server-postgres</code></strong></td><td style="text-align:left;">Direct SQL access to Supabase</td><td style="text-align:left;">Essential for debugging User/Auth data and migrations.</td></tr><tr><td style="text-align:left;"><strong><code>mcp-server-github</code></strong></td><td style="text-align:left;">Manage Issues/PRs/Releases</td><td style="text-align:left;">Track &quot;Beta Feedback&quot; tickets and release changelogs.</td></tr><tr><td style="text-align:left;"><strong><code>mcp-server-sentry</code></strong></td><td style="text-align:left;">Production Error Monitoring</td><td style="text-align:left;">Real-time crash reporting for PWA and Native builds.</td></tr><tr><td style="text-align:left;"><strong><code>mcp-server-vercel</code></strong></td><td style="text-align:left;">Deployment Management</td><td style="text-align:left;">Trigger/Monitor deployments from CLI.</td></tr></tbody></table><h3 id="setup-strategy" tabindex="-1">Setup Strategy <a class="header-anchor" href="#setup-strategy" aria-label="Permalink to &quot;Setup Strategy&quot;">‚Äã</a></h3><ol><li><strong>Postgres</strong>: Configure with <code>DIRECT_URL</code> from <code>.env</code>.</li><li><strong>GitHub</strong>: Authenticate via Personal Access Token (PAT).</li><li><strong>Android (Custom)</strong>: Create <code>scripts/android-bridge.sh</code> to wrap <code>adb</code>/<code>grade</code> for AI control.</li></ol><hr><h2 id="_2-current-mobile-implementation-analysis" tabindex="-1">2. Current Mobile Implementation (Analysis) <a class="header-anchor" href="#_2-current-mobile-implementation-analysis" aria-label="Permalink to &quot;2. Current Mobile Implementation (Analysis)&quot;">‚Äã</a></h2><p><strong>Architecture</strong>: Progress Web Application (PWA) <strong>Stack</strong>: Next.js 15, Serwist (Service Worker), Tailwind CSS.</p><h3 id="assessment" tabindex="-1">Assessment <a class="header-anchor" href="#assessment" aria-label="Permalink to &quot;Assessment&quot;">‚Äã</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">Feature</th><th style="text-align:left;">Status</th><th style="text-align:left;">Notes</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>App Icon</strong></td><td style="text-align:left;">‚úÖ</td><td style="text-align:left;"><code>manifest.json</code> configured with correct sizes/masks.</td></tr><tr><td style="text-align:left;"><strong>Offline Mode</strong></td><td style="text-align:left;">‚ö†Ô∏è</td><td style="text-align:left;">Basic <code>defaultCache</code> active. API caching needs custom rules for &quot;Offline Maps&quot;.</td></tr><tr><td style="text-align:left;"><strong>Installation</strong></td><td style="text-align:left;">‚úÖ</td><td style="text-align:left;"><code>standalone</code> mode supported.</td></tr><tr><td style="text-align:left;"><strong>Push Notifs</strong></td><td style="text-align:left;">‚ö†Ô∏è</td><td style="text-align:left;">Web Push works on Android/iOS 16.4+, but native integration is more reliable.</td></tr><tr><td style="text-align:left;"><strong>Performance</strong></td><td style="text-align:left;">üü¢</td><td style="text-align:left;">Score: High. SSR + Edge Caching.</td></tr></tbody></table><p><strong>Verdict</strong>: The PWA is excellent for immediate delivery but lacks &quot;App Store Presence&quot; and deep native integration (Contacts, Biometrics).</p><hr><h2 id="_3-delivery-roadmap" tabindex="-1">3. Delivery Roadmap <a class="header-anchor" href="#_3-delivery-roadmap" aria-label="Permalink to &quot;3. Delivery Roadmap&quot;">‚Äã</a></h2><h3 id="phase-1-pwa-polish-immediate" tabindex="-1">Phase 1: PWA Polish (Immediate) <a class="header-anchor" href="#phase-1-pwa-polish-immediate" aria-label="Permalink to &quot;Phase 1: PWA Polish (Immediate)&quot;">‚Äã</a></h3><p><em>Goal: Perfect the browser-based &quot;App&quot; experience.</em></p><ul><li>[x] <strong>Install Prompt</strong>: Implement a custom &quot;Install HelpmApp&quot; bottom sheet for iOS/Android users.</li><li>[x] <strong>Offline Maps</strong>: Configure Serwist to cache Map tiles (OpenStreetMap/Mapbox) for offline usage.</li><li>[x] <strong>Splash Screen</strong>: Generate splash screens for iOS (requires specific <code>&lt;link&gt;</code> tags in <code>layout.tsx</code>).</li></ul><h3 id="phase-2-native-lite-weeks-1-4" tabindex="-1">Phase 2: &quot;Native Lite&quot; (Weeks 1-4) <a class="header-anchor" href="#phase-2-native-lite-weeks-1-4" aria-label="Permalink to &quot;Phase 2: &quot;Native Lite&quot; (Weeks 1-4)&quot;">‚Äã</a></h3><p><em>Goal: Submit to Apple App Store &amp; Google Play Store.</em></p><p><strong>Technology</strong>: <a href="https://capacitorjs.com/" target="_blank" rel="noreferrer">Capacitor</a><em>Why Capacitor? It wraps the existing Next.js build into a native container without rewriting code.</em></p><p><strong>Steps</strong>:</p><ol><li>[x] Initialize Capacitor: <code>pnpm add @capacitor/core @capacitor/cli</code>.</li><li>[x] Create Android Project: <code>npx cap add android</code>.</li><li>[ ] Create iOS Project: <code>npx cap add ios</code> (Requires macOS - <strong>PARKED</strong>).</li><li><strong>CI/CD</strong>: specific GitHub Actions for building APKs/IPAs.</li></ol><h3 id="phase-3-native-features-month-2" tabindex="-1">Phase 3: Native Features (Month 2+) <a class="header-anchor" href="#phase-3-native-features-month-2" aria-label="Permalink to &quot;Phase 3: Native Features (Month 2+)&quot;">‚Äã</a></h3><p><em>Goal: Deep system integration.</em></p><ul><li><strong>Biometrics</strong>: Use <code>FaceID</code> for faster Provider login.</li><li><strong>Geolocation</strong>: Background location tracking for &quot;Services near me&quot; alerts.</li><li><strong>Contacts</strong>: Import contacts for referral network.</li></ul><h2 id="_4-immediate-next-steps" tabindex="-1">4. Immediate Next Steps <a class="header-anchor" href="#_4-immediate-next-steps" aria-label="Permalink to &quot;4. Immediate Next Steps&quot;">‚Äã</a></h2><ol><li><strong>Phase 2 Execution</strong>: Focus on Android Capacitor build hardening.</li><li><strong>Notification Strategy</strong>: Polish &quot;Native-like&quot; push notifications (Service Worker).</li><li><strong>Store Assets</strong>: Prepare screenshots and descriptions for Play Store listing.</li></ol>',28)])])}const m=t(i,[["render",s]]);export{h as __pageData,m as default};
