import{_ as a,c as i,o as r,ag as t}from"./chunks/framework._dqZt2de.js";const m=JSON.parse('{"title":"HelpmApp Pitch Deck (Markdown)","description":"","frontmatter":{},"headers":[],"relativePath":"knowledge-base/internal/PITCH_DECK.md","filePath":"knowledge-base/internal/PITCH_DECK.md"}'),n={name:"knowledge-base/internal/PITCH_DECK.md"};function o(l,e,s,d,c,h){return r(),i("div",null,[...e[0]||(e[0]=[t('<h1 id="helpmapp-pitch-deck-markdown" tabindex="-1">HelpmApp Pitch Deck (Markdown) <a class="header-anchor" href="#helpmapp-pitch-deck-markdown" aria-label="Permalink to &quot;HelpmApp Pitch Deck (Markdown)&quot;">​</a></h1><blockquote><p>Use each H2 section as a slide. All figures reflect the Version 2.0 reset (Nov 2025).</p></blockquote><h2 id="_1-opening-title" tabindex="-1">1. Opening / Title <a class="header-anchor" href="#_1-opening-title" aria-label="Permalink to &quot;1. Opening / Title&quot;">​</a></h2><ul><li><strong>HelpmApp:</strong> Connecting the Homeless with Services Through Gamification</li><li><em>Find Help. Earn Rewards. Build Your Future.</em></li><li>Status: Clean-slate rebuild underway (previous code and infra intentionally removed).</li></ul><h2 id="_2-problem" tabindex="-1">2. Problem <a class="header-anchor" href="#_2-problem" aria-label="Permalink to &quot;2. Problem&quot;">​</a></h2><ul><li>[13,231 people slept rough in London in 2024/25](file:///home/a/Code/helpmapp/docs/knowledge-base/evidence/chain-report-2025.md) – highest on record (Source: <a href="./../evidence/chain-report-2025.html">CHAIN Report 2025</a>).</li><li><strong>63% are new to the streets</strong>, often unaware of services.</li><li>10,000+ service slots spread across hundreds of providers, with inconsistent data.</li><li>Users waste time travelling to closed/full services; providers lack utilisation data.</li><li>Digital divide: low-end smartphones, intermittent connectivity, minimal engagement incentives.</li></ul><h2 id="_3-solution" tabindex="-1">3. Solution <a class="header-anchor" href="#_3-solution" aria-label="Permalink to &quot;3. Solution&quot;">​</a></h2><ul><li>Mobile-first PWA (native apps in roadmap) with verified, real-time service listings and offline cache.</li><li><strong>Predictive Prevention:</strong> Data engine designed to identify at-risk users early—proven to reduce homelessness by <strong>71%</strong> (Source: <a href="./../evidence/prevention-unit-la.html">LA Policy Lab</a>).</li><li>Gamified check-ins: users earn stars for visiting services, redeemable for food, hygiene, transport, phone credit.</li><li>Provider + borough dashboards deliver anonymised insights on demand patterns and gaps.</li><li>Governance led by people with lived experience to ensure dignity, privacy, and relevance.</li></ul><h2 id="_4-why-now-differentiation" tabindex="-1">4. Why Now / Differentiation <a class="header-anchor" href="#_4-why-now-differentiation" aria-label="Permalink to &quot;4. Why Now / Differentiation&quot;">​</a></h2><ul><li>Only UK platform blending real-time availability + gamification + actionable data.</li><li>Automated data ingestion/validation lowers maintenance overhead.</li><li>Rewards programme aligned with corporate ESG and sponsor demand.</li><li>Reset enables modern architecture, analytics-first instrumentation, and transparent governance.</li></ul><h2 id="_5-traction-evidence" tabindex="-1">5. Traction &amp; Evidence <a class="header-anchor" href="#_5-traction-evidence" aria-label="Permalink to &quot;5. Traction &amp; Evidence&quot;">​</a></h2><ul><li>Legacy PWA (now archived) proved user demand with 64+ services onboarded.</li><li>Early partnerships: St Mungo&#39;s, The Passage, Westminster, Camden, Lambeth (conversations/LOIs).</li><li>Active community via Telegram and peer advocate model ready for relaunch.</li><li>Data from pilots used to design new impact framework (KPIs for users, system, stakeholders).</li></ul><h2 id="_6-roadmap-18-months" tabindex="-1">6. Roadmap (18 Months) <a class="header-anchor" href="#_6-roadmap-18-months" aria-label="Permalink to &quot;6. Roadmap (18 Months)&quot;">​</a></h2><ol><li><strong>Foundation (Months 1-6):</strong> Rebuild MVP, 500 users, 50 providers (10 premium), £10k rewards value, 3 pilot boroughs.</li><li><strong>Growth (Months 7-12):</strong> Greater London coverage, dashboards v1, 2k users, 3 borough pilots, 7 sponsors.</li><li><strong>Scale (Months 13-18):</strong> Manchester/Birmingham expansion, native apps, public API, 5k users, 200 providers, 360k check-ins.</li></ol><h2 id="_7-business-model" tabindex="-1">7. Business Model <a class="header-anchor" href="#_7-business-model" aria-label="Permalink to &quot;7. Business Model&quot;">​</a></h2><ul><li><strong>Service Providers:</strong> Premium analytics subscriptions (£100-150/mo).</li><li><strong>Local Authorities / GLA / NHS:</strong> Data + reporting contracts (£30-60k per authority annually).</li><li><strong>Corporate Sponsors:</strong> Cash + in-kind rewards bundles (£5-15k each) with measurable impact reporting.</li><li><strong>Grants:</strong> Bridge working capital during growth (UnLtd, DSIT Digital Inclusion, National Lottery, etc.).</li></ul><h2 id="_8-impact-metrics" tabindex="-1">8. Impact Metrics <a class="header-anchor" href="#_8-impact-metrics" aria-label="Permalink to &quot;8. Impact Metrics&quot;">​</a></h2><ul><li><strong>User Outcomes:</strong> &lt;2 min service discovery, 4+ check-ins/user/month, 60% reward redemption, NPS 50+.</li><li><strong>System Impact:</strong> 95% listing completeness, provider dashboard adoption, borough pilots with actionable insights.</li><li><strong>Stakeholder Value:</strong> Sponsor renewal &gt;85%, case studies, contract retention, evidence-based commissioning decisions.</li></ul><h2 id="_9-financial-snapshot" tabindex="-1">9. Financial Snapshot <a class="header-anchor" href="#_9-financial-snapshot" aria-label="Permalink to &quot;9. Financial Snapshot&quot;">​</a></h2><ul><li><strong>Funding Need:</strong> £70k Year 1 working capital (marketing £25k, rewards £20k, partnerships £10k, ops £15k).</li><li><strong>Projected Revenue:</strong> £52k (Year1) → £288k (Year2) → £635k (Year3).</li><li><strong>Projected Expenses:</strong> £85k → £293k → £555k leading to £80k surplus by Year 3.</li><li>Diversified revenue reduces reliance on any single stream.</li></ul><h2 id="_10-team-governance" tabindex="-1">10. Team &amp; Governance <a class="header-anchor" href="#_10-team-governance" aria-label="Permalink to &quot;10. Team &amp; Governance&quot;">​</a></h2><ul><li>Founders with product, engineering, and lived-experience backgrounds.</li><li>Advisory board: major homelessness charities, borough housing leads, technologists, and individuals with lived experience.</li><li>Peer advocate network with stipends, monthly governance/privacy reviews.</li></ul><h2 id="_11-call-to-action" tabindex="-1">11. Call to Action <a class="header-anchor" href="#_11-call-to-action" aria-label="Permalink to &quot;11. Call to Action&quot;">​</a></h2><ul><li><strong>Funders:</strong> Join £70k working capital round (UnLtd, DSIT, Lottery, CSR/angels).</li><li><strong>Service Providers &amp; Boroughs:</strong> Participate in pilot to shape analytics and coordination.</li><li><strong>Corporate Sponsors:</strong> Underwrite rewards catalogue (meals, hygiene, travel, connectivity) with transparent reporting.</li></ul><h2 id="_12-contact" tabindex="-1">12. Contact <a class="header-anchor" href="#_12-contact" aria-label="Permalink to &quot;12. Contact&quot;">​</a></h2><ul><li>Website (legacy): <a href="https://www.helpmapp.org" target="_blank" rel="noreferrer">https://www.helpmapp.org</a></li><li>Telegram: <a href="https://t.me/helpmappuk" target="_blank" rel="noreferrer">https://t.me/helpmappuk</a></li><li>Email: <a href="mailto:hello@helpmapp.org" target="_blank" rel="noreferrer">hello@helpmapp.org</a></li></ul>',26)])])}const u=a(n,[["render",o]]);export{m as __pageData,u as default};
