import{_ as t,c as a,o,ag as i}from"./chunks/framework._dqZt2de.js";const h=JSON.parse('{"title":"Social Media Automation Strategy","description":"","frontmatter":{},"headers":[],"relativePath":"knowledge-base/internal/SOCIAL_MEDIA_STRATEGY.md","filePath":"knowledge-base/internal/SOCIAL_MEDIA_STRATEGY.md"}'),n={name:"knowledge-base/internal/SOCIAL_MEDIA_STRATEGY.md"};function r(l,e,s,u,d,c){return o(),a("div",null,[...e[0]||(e[0]=[i('<h1 id="social-media-automation-strategy" tabindex="-1">Social Media Automation Strategy <a class="header-anchor" href="#social-media-automation-strategy" aria-label="Permalink to &quot;Social Media Automation Strategy&quot;">​</a></h1><p><strong>Goal</strong>: Amplify HelpmApp&#39;s reach with minimal manual effort across diversified channels.</p><h2 id="_1-current-state" tabindex="-1">1. Current State <a class="header-anchor" href="#_1-current-state" aria-label="Permalink to &quot;1. Current State&quot;">​</a></h2><ul><li><strong>Channels</strong>: X (Twitter), Instagram, Threads, Snapchat, TikTok, YouTube, Substack.</li><li><strong>Decommissioned</strong>: Telegram (Phase 12 complete).</li><li><strong>Automation</strong>: <ul><li>Content generation pipeline for &quot;Swiss Social Realism&quot; updates.</li><li>Slack-based administrative alerts for performance and ingestion.</li></ul></li></ul><h2 id="_2-strategic-vision" tabindex="-1">2. Strategic Vision <a class="header-anchor" href="#_2-strategic-vision" aria-label="Permalink to &quot;2. Strategic Vision&quot;">​</a></h2><p>&quot;The Pulse&quot; dashboard should drive social content. When a service is trending, we should broadcast it across high-reach platforms.</p><h3 id="phase-1-content-as-code-maintained" tabindex="-1">Phase 1: Content-as-Code (Maintained) <a class="header-anchor" href="#phase-1-content-as-code-maintained" aria-label="Permalink to &quot;Phase 1: Content-as-Code (Maintained)&quot;">​</a></h3><ul><li><strong>Hook</strong>: When <code>AggregateStats</code> runs (daily), if a service has &gt; X views: <ul><li>Generate a &quot;Trending Service&quot; card image using AI.</li><li>Draft social copy for X and Instagram.</li></ul></li></ul><h3 id="phase-2-platform-integration-current" tabindex="-1">Phase 2: Platform Integration (Current) <a class="header-anchor" href="#phase-2-platform-integration-current" aria-label="Permalink to &quot;Phase 2: Platform Integration (Current)&quot;">​</a></h3><ul><li><strong>Tooling</strong>: Use <strong>Buffer</strong> or native platform APIs where feasible.</li><li><strong>Workflow</strong>: <ol><li>App generates content → Webhook → Review Queue.</li><li>Manual approval to ensure &quot;Swiss Social Realism&quot; quality.</li><li>Auto-post to X, IG, and TikTok.</li></ol></li></ul><h3 id="phase-3-video-automation-long-term" tabindex="-1">Phase 3: Video Automation (Long Term) <a class="header-anchor" href="#phase-3-video-automation-long-term" aria-label="Permalink to &quot;Phase 3: Video Automation (Long Term)&quot;">​</a></h3><ul><li><strong>Concept</strong>: &quot;Service Spotlight&quot; shorts.</li><li><strong>Tech</strong>: Remotion (React Video).</li><li><strong>Flow</strong>: <ol><li>Select Service.</li><li>Remotion generates 15s video (Map zoom + Photos + text).</li><li>Upload to TikTok/YouTube Shorts.</li></ol></li></ul><h2 id="_3-implementation-status" tabindex="-1">3. Implementation Status <a class="header-anchor" href="#_3-implementation-status" aria-label="Permalink to &quot;3. Implementation Status&quot;">​</a></h2><ul><li>[x] <strong>Telegram Decommissioning</strong>: Completed removal of widgets, links, and bot integrations.</li><li>[ ] <strong>Step 1</strong>: Update <code>jobs/social-publisher.ts</code> to support multi-platform delivery.</li><li>[ ] <strong>Step 2</strong>: Integrate Buffer API for unified posting.</li></ul>',14)])])}const m=t(n,[["render",r]]);export{h as __pageData,m as default};
