import{_ as t,c as o,o as a,ag as i}from"./chunks/framework._dqZt2de.js";const g=JSON.parse('{"title":"Social Media Automation Strategy","description":"","frontmatter":{},"headers":[],"relativePath":"knowledge-base/internal/SOCIAL_MEDIA_STRATEGY.md","filePath":"knowledge-base/internal/SOCIAL_MEDIA_STRATEGY.md"}'),r={name:"knowledge-base/internal/SOCIAL_MEDIA_STRATEGY.md"};function n(l,e,s,u,d,c){return a(),o("div",null,[...e[0]||(e[0]=[i('<h1 id="social-media-automation-strategy" tabindex="-1">Social Media Automation Strategy <a class="header-anchor" href="#social-media-automation-strategy" aria-label="Permalink to &quot;Social Media Automation Strategy&quot;">​</a></h1><p><strong>Goal</strong>: Amplify HelpmApp&#39;s reach with minimal manual effort.</p><h2 id="_1-current-state" tabindex="-1">1. Current State <a class="header-anchor" href="#_1-current-state" aria-label="Permalink to &quot;1. Current State&quot;">​</a></h2><ul><li><strong>Channels</strong>: X (Twitter), Telegram, YouTube, TikTok.</li><li><strong>Automation</strong>: <ul><li>GitHub Pushes -&gt; Telegram (Technical Updates).</li><li>New User Signups -&gt; Telegram (Admin Alerts).</li><li>New Provider Requests -&gt; Telegram (Admin Alerts).</li></ul></li></ul><h2 id="_2-strategic-vision" tabindex="-1">2. Strategic Vision <a class="header-anchor" href="#_2-strategic-vision" aria-label="Permalink to &quot;2. Strategic Vision&quot;">​</a></h2><p>&quot;The Pulse&quot; dashboard should drive social content. When a service is trending, we should tweet about it.</p><h3 id="phase-1-content-as-code-immediate" tabindex="-1">Phase 1: content-as-code (Immediate) <a class="header-anchor" href="#phase-1-content-as-code-immediate" aria-label="Permalink to &quot;Phase 1: content-as-code (Immediate)&quot;">​</a></h3><ul><li><strong>Hook</strong>: When <code>AggregateStats</code> runs (daily), if a service has &gt; X views: <ul><li>Generate a &quot;Trending Service&quot; card image.</li><li>Post to Telegram Channel automatically.</li></ul></li></ul><h3 id="phase-2-cross-posting-medium-term" tabindex="-1">Phase 2: Cross-Posting (Medium Term) <a class="header-anchor" href="#phase-2-cross-posting-medium-term" aria-label="Permalink to &quot;Phase 2: Cross-Posting (Medium Term)&quot;">​</a></h3><ul><li><strong>Tooling Recommendation</strong>: <strong>Buffer</strong> or <strong>Typefully</strong> api.</li><li><strong>Why?</strong>: Direct API access to X/TikTok is brittle and expensive for free tiers. Buffer handles the API changes.</li><li><strong>Workflow</strong>: <ol><li>App generates content → Webhook → Buffer Queue.</li><li>Human review in Buffer (Quality Control).</li><li>Auto-post.</li></ol></li></ul><h3 id="phase-3-video-automation-long-term" tabindex="-1">Phase 3: Video Automation (Long Term) <a class="header-anchor" href="#phase-3-video-automation-long-term" aria-label="Permalink to &quot;Phase 3: Video Automation (Long Term)&quot;">​</a></h3><ul><li><strong>Concept</strong>: &quot;Service Spotlight&quot; shorts.</li><li><strong>Tech</strong>: Remotion (React Video).</li><li><strong>Flow</strong>: <ol><li>Select Service.</li><li>Remotion generates 15s video (Map zoom + Photos + text).</li><li>Upload to TikTok/YouTube Shorts.</li></ol></li></ul><h2 id="_3-implementation-plan" tabindex="-1">3. Implementation Plan <a class="header-anchor" href="#_3-implementation-plan" aria-label="Permalink to &quot;3. Implementation Plan&quot;">​</a></h2><ul><li>[ ] <strong>Step 1</strong>: Enhance <code>scripts/notify-telegram.ts</code> to accept generic messages for &quot;Trending&quot; alerts.</li><li>[ ] <strong>Step 2</strong>: Create <code>jobs/social-publisher.ts</code> in <code>apps/worker</code>.</li><li>[ ] <strong>Step 3</strong>: Sign up for Buffer Free Tier and get Email-to-Post or Webhook URL.</li></ul>',14)])])}const h=t(r,[["render",n]]);export{g as __pageData,h as default};
