import{_ as a,c as r,o as i,ag as o}from"./chunks/framework._dqZt2de.js";const t="/assets/16_provider_register.png",n="/assets/8_provider_dashboard.png",m=JSON.parse('{"title":"HelpmApp Provider Onboarding Guide","description":"","frontmatter":{},"headers":[],"relativePath":"onboarding/PROVIDER_GUIDE.md","filePath":"onboarding/PROVIDER_GUIDE.md"}'),s={name:"onboarding/PROVIDER_GUIDE.md"};function l(p,e,d,g,c,h){return i(),r("div",null,[...e[0]||(e[0]=[o('<h1 id="helpmapp-provider-onboarding-guide" tabindex="-1">HelpmApp Provider Onboarding Guide <a class="header-anchor" href="#helpmapp-provider-onboarding-guide" aria-label="Permalink to &quot;HelpmApp Provider Onboarding Guide&quot;">​</a></h1><p>Welcome to HelpmApp! This guide will help you set up your provider account, manage your services, and start connecting with people in need.</p><h2 id="_1-registration" tabindex="-1">1. Registration <a class="header-anchor" href="#_1-registration" aria-label="Permalink to &quot;1. Registration&quot;">​</a></h2><ol><li>Navigate to <a href="https://helpmapp.org/provider/register" target="_blank" rel="noreferrer">https://helpmapp.org/provider/register</a>.</li><li>Fill in your organization details: <ul><li><strong>Company Name</strong>: Your official organization name.</li><li><strong>Contact Email</strong>: This will be your login email.</li><li><strong>Borough</strong>: The primary area you serve.</li></ul></li><li>Submit your request. Our admin team will review and approve your account within 24 hours.</li></ol><p><img src="'+t+'" alt="Registration Page"></p><h2 id="_2-accessing-your-dashboard" tabindex="-1">2. Accessing Your Dashboard <a class="header-anchor" href="#_2-accessing-your-dashboard" aria-label="Permalink to &quot;2. Accessing Your Dashboard&quot;">​</a></h2><p>Once approved, log in at <a href="https://helpmapp.org/provider/login" target="_blank" rel="noreferrer">https://helpmapp.org/provider/login</a>.</p><p><strong>Dashboard Features:</strong></p><ul><li><strong>Overview</strong>: See real-time stats on service views and check-ins.</li><li><strong>My Services</strong>: Manage your service listings.</li><li><strong>Scanner</strong>: Scan user QR codes to verify check-ins (if applicable).</li></ul><p><img src="'+n+'" alt="Provider Dashboard"></p><h2 id="_3-managing-services" tabindex="-1">3. Managing Services <a class="header-anchor" href="#_3-managing-services" aria-label="Permalink to &quot;3. Managing Services&quot;">​</a></h2><h3 id="adding-a-service" tabindex="-1">Adding a Service <a class="header-anchor" href="#adding-a-service" aria-label="Permalink to &quot;Adding a Service&quot;">​</a></h3><ol><li>Click <strong>&quot;Add Service&quot;</strong> on your dashboard.</li><li>Provide detailed information: <ul><li><strong>Name &amp; Description</strong>: Clearly describe what you offer.</li><li><strong>Category</strong>: Choose the most relevant category (e.g., Food, Shelter).</li><li><strong>Location</strong>: Enter the exact address. Use the map pin to confirm coordinates.</li><li><strong>Access Info</strong>: Explain how to access the service (e.g., &quot;Drop-in&quot;, &quot;Referral only&quot;).</li></ul></li><li>Click <strong>&quot;Publish&quot;</strong>. Your service is immediately live on the map.</li></ol><h3 id="updating-availability" tabindex="-1">Updating Availability <a class="header-anchor" href="#updating-availability" aria-label="Permalink to &quot;Updating Availability&quot;">​</a></h3><p>Keep your hours and status up-to-date. Users rely on this information being accurate, especially for emergency services.</p><h2 id="_4-analytics-impact" tabindex="-1">4. Analytics &amp; Impact <a class="header-anchor" href="#_4-analytics-impact" aria-label="Permalink to &quot;4. Analytics &amp; Impact&quot;">​</a></h2><p>View detailed analytics for each service to understand your reach.</p><ul><li><strong>Impressions</strong>: How many times your service appeared in search results.</li><li><strong>Views</strong>: How many users clicked to see details.</li><li><strong>Check-ins</strong>: Number of verified visits.</li></ul><hr><p><strong>Need Help?</strong> Contact us via Telegram <a href="https://t.me/helpmapporg" target="_blank" rel="noreferrer">@helpmapporg</a> or email <a href="mailto:helpmapp@proton.me" target="_blank" rel="noreferrer">helpmapp@proton.me</a>.</p>',20)])])}const v=a(s,[["render",l]]);export{m as __pageData,v as default};
